/*
The camera rotation is stored as yaw/pitch rotations. Yaw is spin and pitch is tilt.
If you need help visualizing this, this image should help:
https://upload.wikimedia.org/wikipedia/commons/c/c1/Yaw_Axis_Corrected.svg
*/

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    // Have the viewing position and direction been initialized?
    if (iFrame > 0) {
        ivec2 address = ivec2(fragCoord);

        // Should this pixel store the view position?
        if (address == VIEW_POSITION_ADDR) {
            fragColor = vec4(getViewPosition(), 1.0);
            mat3 camera = getCamera(getViewDirection());

            // Update the view position based on keypresses:
            if (keyUpPressed()) {
                fragColor.xyz += camera[2] * STEP_SIZE; // Move STEP_SIZE units in the camera's forward direction
            }

            if (keyDownPressed()) {
                fragColor.xyz -= camera[2] * STEP_SIZE; // Move STEP_SIZE units opposite the camera's forward direction
            }

            if (keyRightPressed()) {
                fragColor.xyz += camera[0] * STEP_SIZE; // Move STEP_SIZE units in the camera's right direction
            }

            if (keyLeftPressed()) {
                fragColor.xyz -= camera[0] * STEP_SIZE; // Move STEP_SIZE units opposite the camera's right direction
            }
        }

        // Should this pixel store the view direction?
        if (address == VIEW_DIRECTION_ADDR) {
            // Center and normalize mouse position, multiply by two pi for rotation from -180 to 180, then scale it by ROTATION_SPEED
            vec2 viewDirection = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y * TWO_PI * ROTATION_SPEED;
            fragColor = vec4(viewDirection, 0.0, 1.0);
        }
    }
}

