// Raymarching constants:
const vec3 SMALL_STEP = vec3(0.001, 0.0, 0.0);
const float MAX_TRACE_DISTANCE = 100.0;
const float MIN_HIT_DISTANCE = 0.001;
const int MAX_ITERATIONS = 50;
const float FOCAL_LENGTH = 1.0;

// Math constants (only two pi in this case):
const float TWO_PI = 6.2832;

// Amount by which the camera moves and rotates:
const float STEP_SIZE = 0.1;
const float ROTATION_SPEED = 0.5;

// Addresses to viewing data:
const ivec2  VIEW_POSITION_ADDR = ivec2(0, 0);
const ivec2 VIEW_DIRECTION_ADDR = ivec2(1, 0);

// Key codes from iq's keyboard input demo (https://www.shadertoy.com/view/lsXGzf):
const int    KEY_UP = 38;
const int  KEY_DOWN = 40;
const int KEY_RIGHT = 39;
const int  KEY_LEFT = 37;

// A bunch of crazy macros because they are the only way to define texelFetches in Common :(
#define  getViewPosition() texelFetch(iChannel0, VIEW_POSITION_ADDR, 0).xyz
#define getViewDirection() texelFetch(iChannel0, VIEW_DIRECTION_ADDR, 0).xy

#define     keyUpPressed() bool(texelFetch(iChannel1, ivec2(38, 0), 0).x)
#define   keyDownPressed() bool(texelFetch(iChannel1, ivec2(40, 0), 0).x)
#define  keyRightPressed() bool(texelFetch(iChannel1, ivec2(39, 0), 0).x)
#define   keyLeftPressed() bool(texelFetch(iChannel1, ivec2(37, 0), 0).x)

// Calculates camera axes:
mat3 getCamera(in vec2 viewDirection) {
    vec2 c = cos(viewDirection); vec2 s = sin(viewDirection);
    return mat3(vec3(       c.x, 0.0,         s.x),  // Camera right axis (x)
                vec3(s.x * -s.y, c.y, -c.x * -s.y),  // Camera up axis (y)
                vec3(s.x *  c.y, s.y, -c.x *  c.y)); // Camera forward axis (z)
}

