#define MAXSTEPS 100
#define MAXDIST 150.
#define MINDIST 0.01

//a circle
float dist2circle( in vec2 p,in vec2 center,in float radius ) 
{
    return length(p-center)-radius;
}

//a line segment
float lineSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

//a rectangle
float dis2box( in vec2 p, in vec2 center, in vec2 r )
{
    vec2 d = abs(p-center)-r;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

//define objects by distance function
float getDist(vec2 p){

	float d=dist2circle(p,vec2(.55,.3),.12);
    float d2=dist2circle(p,vec2(.5,.9),.1);
    d=min(d,d2);    
    d2=dist2circle(p,vec2(.15,.5),.1);
    d=min(d,d2);    
    d2=dis2box(p,vec2(0.8,.6),vec2(.1,.07));
    d=min(d,d2);    
	d2=dis2box(p,vec2(0.2,.12),vec2(.1,.07));
    d=min(d,d2);  
	return d;

}
//2D ray marching
float rayMarch2D(vec2 ro,vec2 rd)
{
	 float dO=0.;
     for(int i=0;i<MAXSTEPS;++i){
      vec2 p=ro+rd*dO;
      float ds=getDist(p);
      dO+=ds;
      if (ds<MINDIST || ds>MAXDIST) break;
      }
 return  dO;   

}


