    float dis=rayMarch2D(ro,rd);
    vec2 point=ro+rd*dis;
    d=dist2circle(uv,point,radius);
    d=1.-smoothstep(0.0,0.005,d);
    d+=.5-.5*smoothstep(0.,0.01,dist2circle(uv,vec2(.55,.3),.12));
    d+=.5-.5*smoothstep(0.,0.01,dist2circle(uv,vec2(.5,.9),.1));
    d+=.5-.5*smoothstep(0.,0.01,dist2circle(uv,vec2(.15,.5),.1));
	d+=.5-.5*smoothstep(0.,0.01,dis2box(uv,vec2(.8,.6),vec2(.1,.07)));
  	d+=.5-.5*smoothstep(0.,0.01,dis2box(uv,vec2(.2,.12),vec2(.1,.07)));;

