float ObjDf (vec3 p)
{
  vec4 s;
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    s = Loadv4 (3 + 3 * n);
    q = p - s.xyz;
    d = PrSphDf (q, 0.5 * s.w);
    dMin = SmoothMin (d, dMin, 1.5);
  }
  idObj = 1;
  q = p;
  q.xz = mod (q.xz + 0.5 * obSpc, obSpc) - 0.5 * obSpc;
  d = PrSphDf (q, 0.5 * obSz);
  if (d < dMin) { dMin = d;  idObj = 2; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    dHit += d;
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}


float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
