vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, vn, clCol;
  vec2 q;
  float dstGrnd, f, fd, ff, sd, sh;
  if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    vn = vec3 (0., 1., 0.);
    f = 1. - smoothstep (0.6, 0.9, dstGrnd / dstFar);
    if (f > 0.) vn = VaryNf (2. * ro, vn, f);
    sh = (dstGrnd < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
    col = 0.9 * mix (vec3 (1., 0.8, 0.5), vec3 (0.9, 0.7, 0.5), 0.1 +
       0.5 * smoothstep (0.3, 0.5, Fbm2 (ro.xz)));
    col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.));
    col = mix (col, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
  } else if (rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
