    col += vec3 (1., 1., 0.9) * (0.3 * pow (sd, 512.) + 0.1 * pow (sd, 1024.));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, roo;
  float dstObj, f;
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) {
      col4 = vec4 (1., 0., 1., 0.2);
    } else if (idObj == 2) {
      col4 = vec4 (0.5, 0.55, 0.5, 0.);
      f = 1. - smoothstep (0.6, 0.9, dstObj / dstFar);
