  fricN = 2.;
  fricS = 0.05;
  fricSW = 10.;
  fricT = 0.5;
  fAttr = 0.05;
  fDamp = 0.01;
  grav = 5.;
  p = Loadv4 (3 + 3 * mId);
  rm = p.xyz;
  sz = p.w;
  vm = Loadv4 (3 + 3 * mId + 1).xyz;
  wm = Loadv4 (3 + 3 * mId + 2).xyz;
