// Distort Effect
// Created by Ernst

#define Distortion_Center vec2(0.0, 0.0);
#define Distortion_Multiplier vec2(1.0, 1.0);
#define Distortion_Intensity 0.3
#define Distortion_Scale 0.4

float rcp(float value)
{
    return 1.0 / value;
}

vec2 DistortUV(vec2 uv)
{
    float amount = 1.6 * max(abs(Distortion_Intensity * 100.0), 1.0);
    float theta = Deg2Rad * min(160.0, amount);
    float sigma = 2.0 * tan(theta * 0.5);

    vec2 DistCenter = 2.0 * Distortion_Center - vec2(1.0, 1.0);
    vec2 DistAxis = Distortion_Multiplier;
    float DistTheta = Distortion_Intensity;
    float DistSigma = sigma;
    float DistScale = 1.0 / Distortion_Scale;
    float DistIntensity = Distortion_Intensity * 100.0;

    uv = (uv - 0.5) * DistScale + 0.5;
    vec2 ruv = DistAxis * (uv - 0.5 - DistCenter);
    float ru = length(vec2(ruv));

    if (DistIntensity > 0.0)
    {
        float wu = ru * DistTheta;
        ru = tan(wu) * (rcp(ru * DistSigma));
        uv = uv + ruv * (ru - 1.0);
    }
    else
    {
        ru = rcp(ru) * DistTheta * atan(ru * DistSigma);
        uv = uv + ruv * (ru - 1.0);
    }

    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uvDistorted = DistortUV(uv);
    fragColor = vec4(texture(iChannel0, uvDistorted).xyz, 1.0); 
}

