//////////////////////////////////////////////////////////////////////
//
// spherical arc spline by mattz
//
// License CC BY 4.0 https://creativecommons.org/licenses/by/4.0/
//
// Given two points on a sphere and two tangent vectors, construct
// a smooth spline (orange) out of a pair of equal-radius arcs
// connecting the points.
//
// The orange spline touches the black points, and is tangent
// to the green arrows.
//
// The maroon dots are the arc centers -- each one is an equal
// distance to the black points.
//
//////////////////////////////////////////////////////////////////////

// light pos
vec3 L = vec3(-0.15, 0.3, 1);

const float PI = 3.141592653589793;
const float EPS = 1e-3;

// animation parameters
const float PERIOD = 30.0; // seconds per repeat
const float PFREQ = 5.0;
const float QFREQ = 1.0;

// antialiasing distance
float aa = 0.01;

// should we draw decorations
bool draw_extras = true;

//////////////////////////////////////////////////////////////////////
// rotate about x-axis 

mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}

//////////////////////////////////////////////////////////////////////
// rotate about y-axis 

mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}

//////////////////////////////////////////////////////////////////////
// ray-sphere intersection

const vec4 miss = vec4(-1);

vec4 sphere(in vec3 o, in vec3 d, in vec3 ctr, in float r) {

    vec3 oc = o - ctr;

    float a = dot(d, d);
    float b = 2.0*dot(oc, d);
    float c = dot(oc, oc) - r*r;

    float D = b*b - 4.0*a*c;

    if (D > 0.0) {

        float sqrtD = sqrt(D);

        float t = 0.5 * ( -b - sqrtD ) / a;

        if (t >= 0.0) {
            vec3 n = normalize( oc + t*d );
            return vec4(n, t);	
        }

    }

    return miss;

}

//////////////////////////////////////////////////////////////////////
// arclengh distance between unit vectors on sphere

float sdist(vec3 a, vec3 b) {
    return acos(clamp(dot(a, b), -1.0, 1.0));
}

//////////////////////////////////////////////////////////////////////
// distance to arrow at p0 with tangent l0 

float darrow(vec3 p, vec3 p0, vec3 l0) {

    float k = 0.125;

    vec3 q = cos(k)*p0 + sin(k)*l0;

    vec3 a0 = normalize(cross(p0, l0));
    vec3 l1 = normalize(cross(q, a0));

    float d = abs(dot(p, a0));
    d = max(d, -dot(p, l0));
    d = max(d, -dot(p, l1));

    float dh = 1e5;

    for (float s=-1.0; s<=1.0; s+=2.0) {

        vec3 r = normalize(q + 0.035*s*a0 + 0.4*p0);

        vec3 l2 = normalize(cross(q, r));
        vec3 l4 = normalize(cross(q, l2));
        vec3 l5 = normalize(cross(r, l2));

        float d2 = abs(dot(p, l2));
        d2 = max(d2, dot(p, l4));
        d2 = max(d2, -dot(p, l5));

        dh = min(dh, d2);
        d = max(d, -s*dot(p, l2));

    }

    d = min(d, dh);

    return d;

}

//////////////////////////////////////////////////////////////////////
// data structure to store spline

struct arc_spline {

    vec3 p0, l0, p1, l1, c0, c1, m, w;
    float r;

};

//////////////////////////////////////////////////////////////////////
// compute a spline on the sphere made of two arcs that are tangent 
// to each other have desired tangents at given points
//
// inputs:
//
//   p0: first point on sphere (unit vector)
//   l0: tangent vector at p0 (unit vector with dot(p0, l0) = 0)
//   p1: second point on sphere (unit vector) 
//   l1: tangent vector at p1 (unit vector with dot(p1, l1) = 0)
//
// output: an arc_spline

void compute_spline(in vec3 p0, in vec3 l0,
                    in vec3 p1, in vec3 l1,
                    out arc_spline S) {

    // fill in data members of spline that copy inputs
    S.p0 = p0;
    S.l0 = l0;

    S.p1 = p1;
    S.l1 = l1;

    // compute the line connecting p0 & p1
    vec3 tmp = normalize(cross(p0, p1));

    // special case: p0 and p1 are connected by a segment of a great circle
    // the line from p0 to p1 hits them at the correct tangents 
    if (max(abs(dot(tmp, l0)), abs(dot(tmp, l1))) < EPS) {
        // in this case we set S.r = 0 and set S.w to be the line
        S.w = tmp;
        S.r = 0.0;
        return;
    }

    // compute the points orthogonal to (l0, p0) and (l1, p1), respectively
    vec3 a0 = cross(l0, p0);
    vec3 a1 = cross(l1, p1);

    // we will construct arc centers 
    //
    //   c0 = cos(r) * p0 + sin(r) * a0
    //   c1 = cos(r) * p1 + sin(r) * a1
    //
    // which are a distance r away from p0 & p1 respectively
    // by construction, dot(c0, l0) = dot(c1, l1) = 0
    //
    // now we want to solve for r such that dot(c0, c1) = cos(2*r)
    //
    // start by observing that 
    //
    //   (cos(r)       * a +
    //    cos(r)*sin(r) * b
    //    sin(r)       * c) = cos(2r)
    //
    // where a = dot(p0, p1), b = dot(p0, a1) + dot(p1, a0), and 
    // c = dot(a0, a1).
    //
    // applying the half angle identities and setting  = 2r, we find
    //
    //   a*(1 + cos())/2 + b*sin()/2 + c*(1 - cos())/2 = cos()
    //   a*(1 + cos()) + b*sin()+ c*(1 - cos()) = 2*cos()
    //   (a - c - 2)*cos() + b*sin() = -(a + c)
    //
    // we can rewrite that as
    //
    //   d*cos() + b*sin() = e
    //
    // where d = a - c - 2 and e = -(a + c).
    //
    // finally, we can rewrite that as
    //
    //   *cos( - ) = e
    //
    // where  = sqrt(d + b) and  = atan(b, d).
    // the solution is given by
    //
    //    =   acos(e / )
    //
    // and r = 0.5 * .

    float a = dot(p0, p1);
    float b = dot(p0, a1) + dot(p1, a0);
    float c = dot(a0, a1);

    float d = (a - c - 2.);
    float e = -(a + c);

    float alpha = length(vec2(d, b));
    float phi = atan(b, d); // in [-pi, pi]
    float tau = acos(e/alpha); // in [0, pi]

    // we want the r with the least magnitude so choose tau with the 
    // opposite sign as phi
    float r = 0.5 * (phi > 0. ? phi - tau : phi + tau);

    // now get c0 & c1
    float cr = cos(r);
    float sr = sin(r);

    S.c0 = normalize(cr*p0 + sr*a0);
    S.c1 = normalize(cr*p1 + sr*a1);

    // m is the midpoint of c0 & c1, the point
    // of mutual tangency of the two arcs
    S.m = normalize(S.c0 + S.c1);

    // get the line connecting c0 & c1
    S.w = normalize(cross(S.c0, S.c1));

    // no longer need the sign of r, want it positive to compute distances later
    S.r = abs(r);

    // choose w to point towards p0
    if (dot(S.w, p0) < 0.) { S.w = -S.w; }

}

//////////////////////////////////////////////////////////////////////
// distance to arc on sphere
//
// inputs: 
//
//   p: query point (unit vector)
//   c: arc center point (unit vector)
//   r: arc radius in radians
//   l: lower tangent (unit vector)
//   w: upper tangent (unit vector)

float darc(vec3 p, vec3 c, float r, vec3 l, vec3 w) {

    float d = abs(sdist(p, c) - r);

    d = max(d, -dot(p, l));
    d = max(d, -dot(p, w));

    return d;

}

//////////////////////////////////////////////////////////////////////
// distance from point p to spline S

float dspline(vec3 p, arc_spline S) {

    if (S.r == 0.0) {

        // spline is the great circle segment connecting p0 & p1
        return max(abs(dot(p, S.w)), -min(dot(p, S.l0), dot(p, S.l1)));

    } else {

        // spline is one or two arcs 

        // ignore either arc segment if the midpoint lies at the corresponding endpoint 
        float d = 1e5;

        if (dot(S.m, S.p0) < 1.0 - EPS) { d = min(d, darc(p, S.c0, S.r, S.l0,  S.w)); }
        if (dot(S.m, S.p1) < 1.0 - EPS) { d = min(d, darc(p, S.c1, S.r, S.l1, -S.w)); }

        return d;

    }

}


//////////////////////////////////////////////////////////////////////
// draw diagram on sphere

vec3 draw(vec3 p) {

    //////////////////////////////////////////////////
    // figure out some sample points and tangents

    vec3 p0 = vec3(0, 0, 1);
    vec3 l0 = vec3(0, 1, 0);

    float u = 2.*PI*fract(iTime/PERIOD);

    vec3 p1 = normalize(vec3(0.25, 0.4, 1.0) + vec3(0.3*sin(PFREQ*u), 0.3*cos(PFREQ*u), 0));    
    vec3 q = normalize(vec3(1, 0, 0.5) + vec3(0.2*sin(QFREQ*u + 0.3), 0.2*cos(QFREQ*u + 0.1), 0));
    vec3 l1 = normalize(cross(q, p1));

    //////////////////////////////////////////////////
    // compute and draw the spline

    arc_spline S;    
    compute_spline(p0, l0, p1, l1, S);

    float bblue = 1e5;
    float blue = min(abs(dot(p, l0)), abs(dot(p, l1)));
    float black = min(sdist(p0, p), sdist(p1, p));
    float green = min(darrow(p, p0, l0), darrow(p, p1, l1));  
    float maroon = 1e5;
    float orange = dspline(p, S);

    if (draw_extras && S.r != 0.0) {

        blue = min(blue, max(abs(dot(p, S.w)), -min(dot(p, l0), dot(p, l1))));

        maroon = min(maroon, sdist(p, S.c0));
        maroon = min(maroon, sdist(p, S.c1));
        black = min(black, sdist(p, S.m));

    }

    vec3 color = vec3(1);

    if (!draw_extras) {
        green = 1e5;
    }

    color = min(color, mix(vec3(1), vec3(0, 0.5, 1), 0.4*smoothstep(aa, 0.0, blue - 0.002)));
    color = min(color, mix(vec3(1), vec3(0.1, 0.6, 0), smoothstep(aa, 0.0, green - 0.002)));
    color = min(color, mix(vec3(1), vec3(1.0, 0.4, 0), smoothstep(aa, 0.0, orange - 0.008)));
    color = min(color, mix(vec3(1), vec3(0.7, 0, 0.4), smoothstep(aa, 0.0, maroon - 0.015)));
    color *= smoothstep(0., aa, black - 0.015);

    return color;

}

//////////////////////////////////////////////////////////////////////
// raytrace to sphere and compute color & lighting

vec4 shade(vec3 ro, vec3 rd) {

    // raytrace
    vec4 res = sphere(ro, rd, vec3(0), 1.0);

    // if miss sphere, gray background
    if (res.w < 0.) { return vec4(vec3(0.85), 1); }

    // get intersection point on sphere
    vec3 p = res.xyz;

    // render on sphere
    vec3 color = draw(p);

    // goofy non realistic lighting
    color *= 0.3*dot(p, L) + 0.7;

    return vec4(color, 1);

}


//////////////////////////////////////////////////////////////////////
// stereographic projection

vec3 sphere_from_plane(vec2 q) {

    float qq = dot(q, q);

    return vec3(2.*q, -1.+qq) / (1. + qq);

}

//////////////////////////////////////////////////////////////////////
// pretty vanilla main handles scene transform & mouse interaction

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // distance from camera to sphere centers
    const float cdist = 6.0;

    // center-relative fragment coordinates
    vec2 uv = fragCoord.xy - 0.5*iResolution.xy;

    // focal length in pixels
    float f = 0.45/iResolution.y;

    vec2 theta = vec2(-0.2, 0.15);

    if (max(iMouse.x, iMouse.y) > 20.0) { 
        theta.x = (iMouse.y - .5*iResolution.y) * 3.5/iResolution.y; 
        theta.y = (iMouse.x - .5*iResolution.x) * -5.0/iResolution.x; 
    }

