mat3 rotate(float angle)
{
    float sc = sin(angle);
    float c = cos(angle);
    return mat3 (1.0, 0.0, 0.0, 0.0, c, sc, 0.0, -sc, c);
}

//geometry of the sdf function
float map(vec3 p)
{
   for( int i = 0; i < 12; i ++)
   {
     p= abs(p) - abs(vec3((sin(time + float(i)) + cos (float(i) + p.x+ s +time))* 0.15)) - .1;
     s += .1 ;
   }
   return dot(p ,normalize(vec3(2.,10., 3.))); 
}


//coolest way to calculate a normal that i've ever seen
//source: https://www.shadertoy.com/view/3dyBzc
//thanks gaz
vec3 calcnorm(vec3 value)
{
    vec3 n = vec3(0);
    for(int i = 0; i < 4; i++)
    {
        vec3 eps = 0.001 * (vec3(9 >> i&1, i >> 1 & 1, i & 1) * 2. - 1.);
        n += eps * map(value + eps);
    }
    return normalize(n);
}

void mainImage(out vec4 fragColor, in vec2 texCoords)
{

// ----------- setup -----------
    //correct texture coordinates to the right aspect ratio

    vec2 uv = (2. * texCoords - resolution.xy) / resolution.y;

    //get current direction
    vec3 dir = normalize(vec3(uv, 2));

    //get camera position
    vec3 camPos = vec3(0, 0, -10);

// ----------- raymarching ----------- 
    float dist = 0.;
    vec3 pos = vec3(0);
    for(int i = 0; i < 255; i ++)
    {
        //rotate current entire world before calculating position
        pos = (camPos + dir * dist) * rotate(time + s);
        float curr = map(pos);
        if(curr < EPSILON)
        {

            break;
        }

        if(dist > 100.)
        {
            dist = 0.;
            break;
        }
        dist+= curr;
    }

// ----------- colors / rendering -----------
    //calculate background value
    float backValue = dot(normalize(vec3(sin(time + uv.x),(sin(time)),cos(time)) ),dir);
    vec3 color = vec3 (0, .4, 1. ) * backValue+ vec3(1,.1,.4) * (1.-backValue); 

    //if we hit something
    if(dist > 0. )
    {
        //set color based on background  value
        color = vec3(dot(calcnorm(pos), normalize(vec3(10., 10., 14.))))* (1.-color);
    }

    //render to screen            / gives that 'trippy' effect when rendering
    fragColor = vec4(color , 1) + texture(iChannel0, texCoords/resolution.xy) * 0.75; //+ texture(prevFrame, inData.v_texcoord)* 0.8;
}

