// reference paper: http://hhoppe.com/ravg.pdf
// distance vector to origin(0, 0)
float det(vec2 a, vec2 b) { return a.x * b.y - b.x * a.y; }

vec2 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {
  float a = det(b0, b2), b = 2.0 * det(b1, b0), d = 2.0 * det(b2, b1);
  float f = b * d - a * a;
  vec2 d21 = b2 - b1, d10 = b1 - b0, d20 = b2 - b0;
  vec2 gf = 2.0 * (b * d21 + d * d10 + a * d20);
  gf = vec2(gf.y, -gf.x);
  vec2 pp = -f * gf / dot(gf, gf);
  vec2 d0p = b0 - pp;
  float ap = det(d0p, d20), bp = 2.0 * det(d10, d0p);
  float t = clamp((ap + bp) / (2.0 * a + b + d), 0.0, 1.0);
  return mix(mix(b0, b1, t), mix(b1, b2, t), t);
}

float distToQuadraticBezierCurve(vec2 p, vec2 b0, vec2 b1, vec2 b2) {
  return length(get_distance_vector(b0 - p, b1 - p, b2 - p));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 A = vec2(100., 500.);
  vec2 B = vec2(300., 100.);
  // vec2 B = iMouse.xy;
  vec2 C = vec2(800., 300.);
  vec2 pos = fragCoord.xy;

  vec4 edgeColor = vec4(1., 0., 0., 1.);
  vec4 backgroundColor = vec4(1., 1., 1., 1.);
  float strokeWidth = 20.;

  vec2 vAB = normalize(B - A);
  vec2 vCB = normalize(C - B);
  if (dot((pos - A), vAB) < 0. || dot(pos - C, vCB) < 0.) {
     fragColor = backgroundColor;
  }


  float dist = distToQuadraticBezierCurve(pos, A, B, C);
  float epsilon = fwidth(dist);
  if (dist < strokeWidth + epsilon) {
    float inCurve =
        1. - smoothstep(strokeWidth - epsilon, strokeWidth + epsilon, dist);
    fragColor = mix(backgroundColor, edgeColor, inCurve);
  } else {
    // discard;
    fragColor = backgroundColor;
  }
}

float noise(in vec2 p) {
    return fract(sin(p.x * 17.6452 + p.y * 565.337) * 5425.235);
}

float SmoothNoise(in vec2 p) {
    vec2 luv = smoothstep(0.0, 1.0, fract(p)); // :P
    vec2 cid = floor(p);

    float bl = noise(cid);
    float br = noise(cid + vec2(1.0, 0.0));
    float tl = noise(cid + vec2(0.0, 1.0));
    float tr = noise(cid + vec2(1.0, 1.0));

    return mix(mix(bl, br, luv.x), mix(tl, tr, luv.x), luv.y);
}

float FractalNoise(in vec2 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

