    fragColor.rg += 0.75 * (2.0 - colorChange) / 1.5 * (1.5 - length(uv)) / 1.5;

    float dt = 0.0;
    for (int iter=0; iter < 50; iter++) {
        vec3 p = ro + rd * dt;

        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        float h = FractalNoise(vec2(atan(p.z, p.x), atan(p.y, length(p.xz))), 10.0, 4.0);
        float d = length(p) - 2.0 + h;

        if (d < 0.001) {
            fragColor.rgb = mix(vec3(1.0, 0.0, 0.0), vec3(1.0, 1.0, 0.0) * 2.0 * h * (1.0 + colorChange), h);
            break;
        }

        if (dt > 10.0) {
            break;
        }

        dt += d;
    }
}

