#define pi 3.14159265
#define iterations 2048
#define real -1.40125
#define imaginary 0.0
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.x;

    // Iterate
    vec2 z2 = vec2(0.0);
    vec2 jitter = vec2(sin(float(iFrame%120))-0.5, cos(float(iFrame%60))-0.5)/pi;
    vec2 c = vec2(((uv+vec2(jitter/(iResolution.xy*2.0)))/pow(10.0, 2.0))+(vec2(real, imaginary)/2.0));
	for (int i=1; i<=iterations; i++){
        z2 = vec2(z2.x*z2.x - z2.y *z2.y, 2.0 * z2.x*z2.y);
        z2 += z2+c;
        if(length(z2) > (sin(float(iFrame%60))/pi)+1.0) break;
	}
    // Time varying pixel color
    vec3 col = vec3(length(z2)/log(float(iterations)));

    // LoicVDB: I store the number of samples in the alpha channel
    // LoicVDB: take the previous color and sample count (black and 0 if on first frame)
    vec4 oldFragColor = vec4(0.);
    if(iFrame != 0) oldFragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);

    // LoicVDB:I put it in the fragColor var because I want to keep it even if it's not in a tile
    fragColor = oldFragColor;

    // Output to screen
    fragColor += vec4(col,1.0);
}

