float fractal(vec3 pos){
    // Iterate
    vec2 z2 = vec2(0.0);
    vec2 c = vec2(pos.xy*2.0);
	for (int i=1; i<=iterations; i++){
        z2 = vec2(z2.x*z2.x - z2.y *z2.y, tan((clamp(pos.z*4.0, -pi/2.0, pi/2.0))) * z2.x*z2.y);
        z2 += z2+c;
	}
    // Compute Density
    float escape = clamp((-(log(length(z2))/float(iterations)))-pow(pos.z, 2.0), 0.0, 1.0);
    return escape;
}

