vec3 directLight(vec3 raypos){
    vec3 LightColor = vec3(1.0);
    vec3 LightDirection = normalize(vec3(-1.0, -1.0, 1.0));
    float distorigin = 0.0;
    vec3 density = vec3(0.0);
    vec3 raydirmod = LightDirection*STEP_SIZE*rand();
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<64; i++){
        raypos = raypos+raydirmod;
        density = vec3(fractal(raypos)*DENSITY);
        vec3 absorbance = exp(-density);
        attenuation *= absorbance;
    }
    return LightColor*attenuation;
}

// Compute/March the Ray
vec3 raymarch(vec3 camerapos, vec3 raydir, vec2 coord){
    float distorigin = 0.0;
    //vec3 densityadd = vec3(0.0);
    //vec3 density = vec3(0.0);
    vec3 raypos = camerapos;
    vec3 raydirmod = raydir*rand()*STEP_SIZE*2.0;
    vec3 outCol = vec3(0.0);
    vec3 attenuation = vec3(1.0);
    for(int i=0; i<MAX_MARCHES; i++){
        raypos = raypos + raydirmod;
        // LoicVDB: don't use this, the density doesn't accumulate, its
        // only purpose is to compute absorbance and transmittance
        //densityadd = vec3(fractal(raypos)*DENSITY);
        //density = vec3(density+densityadd);

        // LoicVDB: just use this for density instead:
        vec3 density = vec3(fractal(raypos)*DENSITY);
        vec3 absorbance = exp(-density*STEP_SIZE);
        vec3 transmittance = 1.0-absorbance;
        vec3 directlighting = attenuation*transmittance;
        if((density.r+density.g+density.b) > 0.0){
            directlighting = directlighting*directLight(raypos);
        }
        outCol += directlighting;
        attenuation *= absorbance;
        distorigin = raypos.z-camerapos.z;
        if(distorigin>MAX_DISTANCE) break;
    }
