	ns = (ns<<13U)^ns;
    ns = ns*(ns*ns*15731U+789221U)+1376312589U;
}
float rand(){
    RNGupdate();
    return float((ns*ns)&uvec2(0x7fffffffU))/float(0x7fffffff);
}

