  nbEdge = floor (pow (float (nBall), 1./3.) + 0.1);
  mIdf = float (mId);
  rm = 0.9 * (floor (vec3 (mod (mIdf, nbEdge), mod (mIdf, nbEdge * nbEdge) / nbEdge,
      mIdf / (nbEdge * nbEdge))) - 0.5 * (nbEdge - 1.));
  vm = 2. * normalize (vec3 (Hashff (mIdf), Hashff (mIdf + 0.3), Hashff (mIdf + 0.6)) - 0.5);
  rad = 1. - 0.2 * Hashff (mIdf + 0.1);
}

const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec3 rm, vm, v;
  vec2 iFrag;
  float rad, vvSum;
  int mId, pxId;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 2 * nBall) discard;
  doInit = (iFrame <= 5);
  hbSize = vec3 (1. * floor (pow (float (nBall), 1./3.)));
  mId = pxId / 2;
  if (doInit) {
    Init (mId, rm, vm, rad);
  } else {
    Step (mId, rm, vm, rad);
    vvSum = 0.;
    for (int n = 0; n < nBall; n ++) {
      v = Loadv4 (2 * n + 1).xyz;
      vvSum += dot (v, v);
    }  
    vm *= 1. / sqrt (vvSum / float (nBall));
  }
  Savev4 (pxId, ((pxId - 2 * mId == 0) ? vec4 (rm, rad) : vec4 (vm, 0.)), fragColor, fragCoord);
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) /
     txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

