//https://www.shadertoy.com/embed/WsKBzG?gui=true&t=1&paused=false&muted=false

#define S(a,b,t) smoothstep(a,b,t)
#define START_TIME  10. 

float DistLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa,ba)/dot(ba,ba), 0., 1.);
    return length(pa-ba*t);
}
float N21(vec2 p) {
    p = fract(p*vec2(233.34, 851.73));
    p += dot(p, p+23.456);
    return fract(p.x*p.y);
}
vec2 N22(vec2 p) {
    float n = N21(p);
    return vec2(n, N21(p+n));
}
vec2 GetPos(vec2 id, vec2 offs) {
    /*
    vec2 n = N22(id);
    float x = sin(iTime*n.x);
    float y = cos(iTime*n.y);    
    return vec2(x,y)*.4;
    */
    // same as above

    /*vec2 n = N22(id+offs)*(iTime+START_TIME);
    return offs+sin(n)*.4;*/
    vec2 n = N22(id+offs)*(iTime+START_TIME);
    return offs+sin(n)*.4;
}
vec2 GetPosNew(vec2 id, vec2 offs) {

    vec2 n = N22(id+offs)*(sin((iTime+START_TIME))*0.5+2.2); // 0.5+1.5 regulates randomness of movements
    float radial = sin(length(id+offs)-(iTime+START_TIME/2.))*2.;
    return offs+sin((n*1.2)+vec2(radial,radial))*.4;//+offs+sin(n)*.4; 

}
vec4 Line(vec2 p, vec2 a, vec2 b){
    float d = DistLine(p,a,b);
    float m = S(.05, .01, d);
    float d2 = length(a-b);
    m *= S(1.5, .8, d2)*.5;
    vec3 col = mix(vec3(0.38,0.87,1.0), vec3(0.945,0.161,1.000), d2*d2*d2*d2-0.2);

    return vec4(col,m*1.3);
}

vec3 Layer(vec2 uv, float fft) {
    vec3 m = vec3(0.);

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    // points
    vec2 p[9];
    int i = 0;
    for(float y=-1.; y<=1.; y++) {
        for(float x=-1.; x<=1.; x++) {
            p[i++] = GetPosNew(id, vec2(x,y));
        }        
    }

    // light waves
    float radial = abs( (sin(length(uv)-(iTime+START_TIME/2.))));
    float depth = fract((uv.y/22.-0.19))*1.5;
    float r = radial * (1.0-length(uv/20.))*0.5;
    r = r*r + depth*depth;
    //m+= r*r;
    r = radial*0.1*r;//*(uv.y+5.5);;
    m += r *vec3(1.000,0.561,0.965) *  (fft);

    // main part
    float t = iTime*10.;
    for(int i=0; i<9; i++){
        vec4 l = Line(gv, p[4], p[i]);
        m += l.w*l.rgb;

        vec2 j = (p[i]-gv) * (15. -fft*6.);
        float sparkle = 1./dot(j,j);


        m += clamp(sparkle*vec3(0.137,0.416,0.482), 0., 1.);// *(sin(t+fract(p[i].x)*10.)*.2+.8)
    }

    vec4 l = Line(gv, p[1], p[3]);
    m += l.w*l.rgb;
    l = Line(gv, p[1], p[5]);
    m += l.w*l.rgb;
    l = Line(gv, p[7], p[3]);
    m += l.w*l.rgb;
    l = Line(gv, p[7], p[5]);
    m += l.w*l.rgb;

    return vec3(m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 gridUv = uv*21.-7.;
    vec2 mouse = (iMouse.xy/iResolution.xy)-.5;

	float fft = texture( iChannel0, vec2(0.02,0.25) ).x; 
    fft = (fft == 0.0) ? 0.3 : fft; // dirty hack when music is not playing

	vec3 col = Layer(gridUv-mouse, fft)*1.; 

    float gradient = uv.y;
    vec3 fftcol = normalize(vec3( 4.0*fft*(1.0-fft), 1.0-fft, fft )) ;
    gradient = gradient*.8- .1;
    gradient *= fft*1.3;
    col -= gradient*fftcol*0.2;


    // test uvs
    //vec2 gv = fract(gridUv-mouse)-.5;
    //if (gv.x>0.4||gv.y>0.4) col = vec3(0.122,0.000,0.000);

    fragColor = vec4(col,1.0);
}

