// Please Enable music on Buffer A iChannel0 (press pause and press play again), idk why it's not playing sometimes
// If someone knows how to fix this soundcloud issue, please let me know
// Main code is in Buffer A based on "Plexus (universe within)" by BIgWings. https://www.youtube.com/watch?v=3CycKKJiwis
// Based on this look https://gist.github.com/ylegall/ce09fad5dfea15a46abbe8cf3f47e613
// Common, Buffer B, Image stages are fork of https://www.shadertoy.com/view/ts2yWm
// 2020-12-08 21:04:39

// shader uniforms 
#define R vec4(iResolution.xy, mod(iTime, 600.), 0)
#define D iChannel0

// ^ R.w is the shader stage in the actual intro,
//   so intead of having 3 different shaders (one for each stage),
//   I only have a single shader which I run in a loop.
//   This approach makes the framework more compact
//   and allowes for a lot of minification in the shader.

// global variables
float P,T;   // pi, tau
vec4 r,c,m;  // random vector, color, mask

// shuffle function
void S()
{
    // hash global "random vector" variable
    r=fract(1e4*sin(r)+r.wxyz);
}

// polar function
vec2 pl(float a)
{
    // angle -> point on unit circle
    return vec2(cos(a),sin(a));
}

// rotation functions
mat3 rx(float a){return mat3(1,0,0,0,cos(a),sin(a),0,-sin(a),cos(a));}
mat3 ry(float a){return mat3(cos(a),0,sin(a),0,1,0,-sin(a),0,cos(a));}

// 2D hash function
vec2 H(vec2 p)
{
    vec3 r=vec3(p,1);

    for(int i=0;i<4;i++)
        r=fract(1e4*sin(r)+r.yzx);

    return r.xy;
}

// 2D continuous noise function
vec2 N(vec2 p)
{
    vec2 i=floor(p),f=p-i,o=vec2(0,1);

    return mix(mix(H(i),
                   H(i+o),f.y),
               mix(H(i+o.yx),
                   H(i+o.y),f.y),f.x);
}

// 2D continuous perlin noise function
vec2 pr(vec2 u)
{
    vec2 y=.0*u,n=y+2.;

    for(int i=0;i<8;i++)
        y+=N(u*n)/n,n*=2.;

    return y;
}

