void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ivec2 fragTexAddr = ivec2(fragCoord);
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    if (iFrame == 0) {
        if (fragTexAddr.x < linkages.length() && fragTexAddr.y == 0) {
            fragColor.xy = linkages[fragTexAddr.x].xy;
        }
    }

    if (iFrame > 0) {
        if (fragTexAddr.x < linkages.length() && fragTexAddr.y == 0) {
            vec2 linkage = getLinkage(fragTexAddr.x);

            // Total force:
            float ftotal = 0.0;

            // Gravitational force:
            ftotal += -cos(linkage.x) * GRAVITY;

            // The linkage should tend towards aligning with the linkage before it:
            if (fragTexAddr.x > 0) {
                int linkageBeforeIndex = fragTexAddr.x - 1;
                vec2 linkageBefore = getLinkage(linkageBeforeIndex);

                // Centrifugal force:
                ftotal += -sin(linkage.x - linkageBefore.x) * linkages[linkageBeforeIndex].w * linkageBefore.y * linkageBefore.y * linkages[linkageBeforeIndex].z;
            }

            // The linkage should be pulled by the linkage after it:
            if (fragTexAddr.x < linkages.length() - 1) {
                int linkageAfterIndex = fragTexAddr.x + 1;
                vec2 linkageAfter = getLinkage(linkageAfterIndex);

                // Centrifugal force:
                ftotal += cos(linkage.x - linkageAfter.x) * linkages[linkageAfterIndex].w * linkageAfter.y * linkageAfter.y * linkages[linkageAfterIndex].z;
            }

            // Acceleration:
            linkage.y += ftotal / linkages[fragTexAddr.x].w;

            // Friction:
            linkage.y *= FRICTION; // How to include centripetal force?

            linkage.x += linkage.y;
            fragColor = vec4(linkage, 0.0, 1.0);
        }
    }
}

