/** Roughness value only affects Oren-Nayar Shading */
#define ROUGHNESS 0.5

const float PI = 3.14159265359;

float degToRad(float d) 
{
    return d * PI / 180.;
}

mat4 makeYRotation(in float angleInRadians) 
{
  float c = cos(degToRad(angleInRadians));
  float s = sin(degToRad(angleInRadians));
  mat4 r;
  r[0].xyzw = vec4(c, 0, -s, 0);
  r[1].xyzw = vec4(0, 1, 0, 0);
  r[2].xyzw = vec4(s, 0, c, 0);
  r[3].xyzw = vec4(0, 0, 0, 1);

  return r;
}

vec3 opTx( vec3 p, mat4 m )
{
    vec3 q = mat3(inverse(m))*p;
    return q;
}

float sdSphere( vec3 p, float s)
{
	return length(p + vec3(.0, .5,0.0))-s * sin(iTime);
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p + vec3(.0, .5, .0))-b,.0))-r;
}

float smin( float a, float b )
{
	float k = .1;
    float h = clamp( .5+.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float map( in vec3 p)
{
	float res = .0;
	float resSph = sdSphere(p, 2.5);

	vec3 q = opTx(p, makeYRotation(5. * iTime));
	float resCube = udRoundBox(q, vec3(1.5), .3);

	res = smin(resCube, resSph);

	return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(.002,.0,.0);

	return normalize( vec3(
           map(pos+eps.xyy) - map(pos-eps.xyy),
           map(pos+eps.yxy) - map(pos-eps.yxy),
           map(pos+eps.yyx) - map(pos-eps.yyx) ) );
}

float intersect( in vec3 ro, in vec3 rd )
{
    const float maxd = 10.;
	float h          = 1.;
    float t          = .0;
    for( int i=0; i<50; i++ )
    {
        if( h<.001 || t>maxd ) break;
	    h = map( ro+rd*t);
        t += h;
    }

    if( t>maxd ) t=-1.;

    return t;
}

float OrenNayar(vec3 ld,vec3 vd,vec3 sn,float r,float a) {
  float LdotV = dot(ld, vd);
  float NdotL = dot(ld, sn);
  float NdotV = dot(sn, vd);

  float s = LdotV - NdotL * NdotV;

  float sigma2 = r * r;
  float A = 1. - .5 * (sigma2/(((sigma2 + .33) + .000001)));
  float B = .45 * sigma2 / ((sigma2 + .09) + .00001);

  float ga = dot(vd-sn*NdotV,sn-sn*NdotL);

  return max(0., NdotL) * (A + B * max(0., ga) * sqrt(max((1.0-NdotV*NdotV)*(1.0-NdotL*NdotL), 0.)) / max(NdotL, NdotV));
}

vec2 res;
float limit = 1.;
vec3 lighting(in vec3 n, in vec3 lp, in vec3 p, float fragCoord)
{

    vec3 ld = normalize(lp - p);
    vec3 vd = normalize(vec3(0., 0., 5.) - p);
    if(iMouse.x == 0.)
    {
        limit = 0.0;
    }
    else{
    	limit = iMouse.x/res.x;
    	limit = limit * 2. - 1.;
    	limit *= 16./9.;
    }
    float diff;
    if(fragCoord <= limit)
    {
		diff = OrenNayar(ld, vd, n, ROUGHNESS, 1.);
    }
    else
    {
        diff = max(dot(ld, n), 0.);
    }

	return vec3(diff);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
