float Maxv2 (vec2 p);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
mat3 StdVuMat (float el, float az);
mat3 QtToRMat (vec4 q);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
void HexVorInit ();
vec4 HexVor (vec2 p);
float Hashfv2 (vec2 p);
vec2 Hashv2v2 (vec2 p);
float Noisefv2 (vec2 p);
float Fbm2 (vec2 p);
vec4 Loadv4 (int idVar);

const ivec3 nbEdge = ivec3 (5);
const int nBall = nbEdge.x * nbEdge.y * nbEdge.z;

vec3 ltPos, vnBall, hbSize, blkAng, vnBlk;
float dstFar, hbMarg, bCylRad, bCylHt, baseRad, baseUp;
int idObj, idBall;
const float pi = 3.14159, sqrt3 = 1.73205;
const int txOff = 4;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float ObjDf (vec3 p)
{
  vec4 sLen;
  vec3 q;
  float dMin, d, tr, f, b;
  dMin = dstFar;
  p.y -= baseUp;
  q = abs (OrAng (p));
  sLen = vec4 (hbSize - hbMarg - 0.1, 0.);
  d = min (min (
     PrRoundBoxDf (q - sLen.wyz, sLen.xww + 0.05, 0.1),
     PrRoundBoxDf (q - sLen.xwz, sLen.wyw + 0.05, 0.1)),
     PrRoundBoxDf (q - sLen.xyw, sLen.wwz + 0.05, 0.1));
  DMIN (1);
  b = length (hbSize.xy - hbMarg);
  f = 0.5 * (baseUp - 0.05);
  tr = 0.25;
  sLen.xyz = vec3 (b + 6. * tr, f - tr, hbSize.z - hbMarg + 2. * tr);
  d = PrCylDf (vec3 (q.xy, abs (q.z) - sLen.z + tr), 0.8 * tr, tr);
  DMIN (3);
  q = p;
  q.xz = Rot2D (q.xz, blkAng.y);
  q.yz = Rot2D (q.yz, blkAng.x);
  d = min (PrRoundBoxDf (vec3 (q.xy, abs (q.z) - sLen.z),
     vec3 (b + 4. * tr, tr, tr) - 0.1, 0.1),
     PrRoundBoxDf (vec3 (abs (q.x) - b - 3. * tr, q.yz),
     vec3 (tr, tr, sLen.z + tr) - 0.1, 0.1));
  DMIN (2);
  d = PrCylDf (vec3 (abs (q.x) - b - 4.5 * tr, q.yz).yzx, 0.8 * tr, tr);
  DMIN (3);
  q = p;
  q.xz = Rot2D (q.xz, blkAng.y);
  d = PrRoundBoxDf (vec3 (abs (q.xz) - sLen.xz, q.y + sLen.y).xzy,
     vec3 (tr, f, tr) - 0.1, 0.1);
  d = min (d, PrRoundBoxDf (vec3 (abs (q.x) - sLen.x, abs (q.y + sLen.y) - sLen.y, q.z),
     vec3 (tr, tr, sLen.z + tr) - 0.1, 0.1));
  d = min (d, PrRoundBoxDf (vec3 (q.xz, q.y + 2. * sLen.y).xzy,
     vec3 (sLen.x, tr, tr) - 0.1, 0.1));
  DMIN (2);
  d = PrCylDf (vec3 (q.xz, q.y + 2. * sLen.y), 2. * tr, 1.4 * tr);
  DMIN (3);
  q = p;
  q.y -= - 2. * f - 0.05;
  d = PrRoundCylDf (q.xzy, baseRad, 0.1, 0.15);
  DMIN (4);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    u = ro - p.xyz;
    rad = 0.46 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnBall = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
} 

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    u = ro - p.xyz;
    rad = 0.46 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (txOff + 4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return step (0., sign (a.x) * sign (a.y));
}

float BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = (hbSize - hbMarg - 0.04) / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

vec4 InCylHit (vec3 ro, vec3 rd)
{
  vec3 s, vn;
  float d, a, b, w, srdy;
  d = dstFar;
  vn = vec3 (0.);
  a = dot (rd.xz, rd.xz);
  b = dot (rd.xz, ro.xz);
  w = b * b - a * (dot (ro.xz, ro.xz) - bCylRad * bCylRad);
  if (w > 0.) {
    srdy = sign (rd.y);
    if (a > 0.) {
      d = (- b + sqrt (w)) / a;
      s = ro + d * rd;
    } else s.y = bCylHt;
    if (abs (s.y) < bCylHt) vn.xz = - s.xz / bCylRad;
    else {
      d = (- srdy * ro.y + bCylHt) / abs (rd.y);
      vn.y = - srdy;
    }
  }
  return vec4 (d, vn);
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.5 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.001 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec4 vc, db4;
  vec3 ltDir, vn, col, rg, vnCyl;
  vec2 qw, wSize;
  float dCyl, hy, y, b, a, f, ga, ltDist, sh;
  bool isLit;
  isLit = true;
  sh = 1.;
  db4 = InCylHit (ro + vec3 (0., - bCylHt, 0.), rd);
  dCyl = db4.x;
  vnCyl = db4.yzw;
  if (vnCyl.y == 0.) {
    ro += dCyl * rd;
    vn = vnCyl;
    hy = mod (ro.y / bCylHt + 0.5, 1.) - 0.5;
    a = atan (vn.x, - vn.z) / pi;
    wSize = vec2 (0.28, 0.33);
    qw = abs (vec2 (mod (64. * 0.5 * (1. + a) + 0.5, 1.), abs (hy)) - 0.5) - 0.5 * wSize;
    if (Maxv2 (abs (qw) - wSize * vec2 (0.43, 0.47)) < 0.) {
      col = vec3 (0.1, 0.2, 0.5);
      isLit = false;
    } else {
      col = vec3 (0.5, 0.6, 0.5);
      if (Maxv2 (qw - 0.5 * wSize) > 0.) {
        col = vec3 (0.7, 0.6, 0.6);
        y = abs (abs (hy) - 0.5) - 0.5;
        if (y > - 0.3/16.) {
          col *= 0.9;
          vn.xz = Rot2D (vn.xz, - pi * a);
          vn.yz = Rot2D (vn.yz, 0.15 * pi * (1. - 2. * SmoothBump (0.15, 0.25, 0.05,
             16. * (0.5 - y))) * sign (hy));
          vn.xz = Rot2D (vn.xz, pi * a);
        } else {
          qw = 16. * vec2 (12. * a, hy);
          rg = ShStagGrid (qw);
          col *= rg.y * (1. - 0.2 * Noisefv2 (16. * qw));
          rg.xz *= sign ((abs (vn.x) > abs (vn.z)) ? vn.x : vn.z);
          if (abs (vn.x) > abs (vn.z)) {
            if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
            else vn.xz = Rot2D (vn.xz, rg.x);
          } else {
            if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
            else vn.zx = Rot2D (vn.zx, rg.x);
          }
        }
      }
    }
  } else if (vnCyl.y > 0.) {
    ro += dCyl * rd;
    HexVorInit ();
    vc = HexVor (0.7 * ro.zx);
    b = 1. - smoothstep (-0.2, -0.05, rd.y) * smoothstep (0.4, 0.8, dCyl / (1.6 * bCylRad));
    vn = mix (vec3 (0., 1., 0.), normalize (vec3 (- vc.y, 4., - vc.z)),
       b * SmoothBump (0.05, 0.13, 0.02, vc.x));
    col = vec3 (0.65, 0.62, 0.62) * (0.7 + 0.3 * b * step (0.06 + 0.03 * vc.w, vc.x) *
       (1. - 0.3 * Noisefv2 (16. * ro.xz)));
  } else {
    ro += dCyl * rd;
    f = 0.;
    qw = ro.xz / bCylRad;
    ga = 2.39996;
    for (float n = 0.; n < 128.; n ++) f += 1. - smoothstep (0.02, 0.025,
       length (qw - sqrt (n / 128.) * sin (n * ga + vec2 (0.5 * pi, 0.))));
    col = mix (vec3 (0.2, 0.2, 0.4), vec3 (1., 1., 0.7), f);
    isLit = false;
  }
  if (isLit) {
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    sh = (vnCyl.y > -0.5) ? min (ObjSShadow (ro + 0.01 * ltDir, ltDir, ltDist), 0.6 + 
       0.4 * BallHitSh (OrAng (ro + vec3 (0., - baseUp, 0.)), OrAng (ltDir), 0.7 * ltDist)) : 1.;
    col = col * (0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.)) +
       0.1 * step (0.95, sh) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, rob, rdb, vn, ltDir;
  float dstBall, dstObj, db, ltDist, nDotL;
  rob = OrAng (ro - vec3 (0., baseUp, 0.));
  rdb = OrAng (rd);
  db = BlkHit (rob, rdb);
  dstBall = (db < dstFar) ? BallHit (rob, rdb) : dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstObj < min (dstBall, dstFar) && idObj == 4 && length (ro.xz + dstObj * rd.xz) < 
     baseRad - 0.1) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    rob = OrAng (ro - vec3 (0., baseUp, 0.));
    rdb = OrAng (rd);
    db = BlkHit (rob, rdb);
    dstBall = (db < dstFar) ? BallHit (rob, rdb) : dstFar;
    dstObj = ObjRay (ro, rd);
  }
  if (dstObj < min (dstBall, dstFar)) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col4 = vec4 (0.85, 0.8, 0.8, 0.2);
    else if (idObj == 2) col4 = vec4 (0.8, 0.8, 0.9, 0.2);
    else if (idObj == 3) col4 = vec4 (0.8, 0.7, 0.7, 0.2);
    else if (idObj == 4) col4 = vec4 (0.9, 0.9, 0.95, 0.2);
  } else if (dstBall < dstFar) {
    ro += dstBall * rd;
    vn = OrAngT (vnBall);
    col4 = vec4 (HsvToRgb (vec3 (mod (33. * float (idBall) / float (nBall), 1.),
       0.1 + 0.9 * BallChqr (idBall, vnBall), 0.9)), 0.2);
  }
  ltDir = ltPos - ro;
  ltDist = length (ltDir);
  ltDir /= ltDist;
  if (min (dstBall, dstObj) < dstFar) {
    nDotL = max (dot (vn, ltDir), 0.);
    if (dstObj < dstBall) nDotL *= nDotL;
    col = col4.rgb * (0.2 + 0.2 * max (dot (vn, ltDir * vec3 (-1., 1., -1.)), 0.) + 0.8 * nDotL) +
       col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  } else col = BgCol (ro, rd);
  if (db < min (dstObj, dstFar)) {
    ro = rob + db * rdb;
    ltDir = ltPos - ro;
    ltDist = length (ltDir);
    ltDir /= ltDist;
    col *= 0.95;
    vn = OrAngT (vnBlk);
    col = mix (col, vec3 (0.7, 0.7, 0.75) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.), pow (1. - abs (dot (rd, vn)), 5.));
  }
  return clamp (col, 0., 1.);
}

vec3 ShowWg (vec2 uv, vec2 canvas, vec3 col, float slVal, float msMode)
{
  vec4 wgBx[2];
  vec3 c;
  vec2 ut, ust;
  float asp;
  asp = canvas.x / canvas.y;
  wgBx[0] = vec4 (0.45 * asp, 0., 0.015 * asp, 0.2);
  wgBx[1] = vec4 (0.45 * asp, -0.44, 0.02, 0.);
  c = vec3 (0.3, 1., 0.3);
  ut = 0.5 * uv - wgBx[0].xy;
  ust = abs (ut) - wgBx[0].zw * vec2 (0.7, 1.);
  if (Maxv2 (ust) < 0.) col = (Minv2 (abs (ust)) * canvas.y < 2.) ? 0.8 * c : vec3 (0.4);
  ut.y -= (slVal - 0.5) * 2. * wgBx[0].w;
  if (abs (length (ut * vec2 (1., 2.)) - 0.02) * canvas.y < 2.) col = c;
  ut = 0.5 * uv - wgBx[1].xy;
  if (length (ut) < wgBx[1].z) col = mix (((msMode > 0.) ? c.grb : c),
     vec3 (0.4), smoothstep (2., 4., abs (length (ut) - 0.02) * canvas.y));
  return col;
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 col, rd, ro;
  vec2 canvas, uv, uvv;
  float el, az, asp, zmFac, sr, slVal, msMode;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  hbSize = stDat.xyz;
  baseRad = 2.8 * hbSize.x;
  msMode = stDat.w;
  stDat = Loadv4 (1);
  blkAng = stDat.xyz;
  slVal = blkAng.z / (2. * pi);
  stDat = Loadv4 (2);
  stDat = Loadv4 (3);
  el = stDat.x;
  az = stDat.y;
  vuMat = StdVuMat (el, az);
  bCylRad = 56.;
  bCylHt = 14.;
  ro = vuMat * vec3 (0., -0.2 * bCylHt, -0.95 * bCylRad);
  hbMarg = 0.4;
  baseUp = 1.2 * length (hbSize - hbMarg) + 0.05;
  ro.y += 0.5 * bCylHt;
  zmFac = 5.5;
  dstFar = 20. * length (hbSize);
  ltPos = vec3 (0., 1.8 * bCylHt, -0.9 * bCylRad);
  ltPos.xz = Rot2D (ltPos.xz, 0.2 * pi);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize (vec3 ((2. * tan (0.5 * atan (uvv.x / asp))) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  col = ShowWg (uv, canvas, col, slVal, msMode);
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec3 OrAng (vec3 p)
{
  p.xz = Rot2D (p.xz, blkAng.y);
  p.yz = Rot2D (p.yz, blkAng.x);
  p.xy = Rot2D (p.xy, blkAng.z);
  return p;
}

vec3 OrAngT (vec3 p)
{
  p.xy = Rot2D (p.xy, - blkAng.z);
  p.yz = Rot2D (p.yz, - blkAng.x);
  p.xz = Rot2D (p.xz, - blkAng.y);
  return p;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. -
     3.) - 1., 0., 1.), c.y);
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

vec2 gVec[7], hVec[7];

void HexVorInit ()
{
  vec3 e = vec3 (1., 0., -1.);
  gVec[0] = e.yy;
  gVec[1] = e.xy;
  gVec[2] = e.yx;
  gVec[3] = e.xz;
  gVec[4] = e.zy;
  gVec[5] = e.yz;
  gVec[6] = e.zx;
  for (int k = 0; k < 7; k ++) hVec[k] = HexToPix (gVec[k]);
}

vec4 HexVor (vec2 p)
{
  vec4 sd, udm;
  vec2 ip, fp, d, u;
  float amp, a;
  amp = 0.7;
  ip = PixToHex (p);
  fp = p - HexToPix (ip);
