void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = u / R,
          P = ( u + vec2(iFrame,0) );
    if (iMouse.z>0.) P /= 2.;                                 // zoom pattern
    float g = 1. + 2.*U.y; // exp2(3.*U.y);
    int L = int(R.x/2.),
        x = int (u.x) - L,
        t = (iFrame/256)%4;                                   // hack to emulate a 1024 white noise texture

    O-=O;
    if      (x==  0) O.r = 1.;                                // inject texture on right subwindow column
    else if (x== -1) O.w = step( g * texelFetch(iChannel1, ivec2(P)%256 , 0)[t] , 1. ); // white noise
    else if (x==L-1) O.w = step( g * texelFetch(iChannel2, ivec2(P)%1024, 0).x  , 1. ); // blue noise
    else O = T(u+vec2(1,0));                                  // scroll

    u.x++;
    if (u.y==.5) O.yz++;                                      // seed fluid for percolation in .yz
    else 
        if(T(u).w > 0. &&  abs(x) > 1  ) {
#define pull(l,x,y)  O.l = max( O.l, T(u+vec2(x,y)).l )       // pull contrib from(x,y)
            for( int i=0; i<6; i++ ) pull(y, i%3-1, i/3-1 );  // 8-connexity layer in y
            pull(z, 0,-1); pull(z, -1,0); pull(z, 1,0);       // 4-connexity layer in z
        }
}

