mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }
const float PI = 3.141592653;
#define sat(a) clamp(a, 0.,1.)

float cir(vec2 p, float r, float amp)
{
    p *= .5;
    float sspd = (amp < 1. ? 1. : -1.);
    float an = atan(p.y, p.x)+PI*.75+iTime*(r*.1+.5)+iTime*sspd;
    float v = sin(an*10.);

    float sCnt = 5.;

    float freq = texelFetch(iChannel0, ivec2(int(sCnt*abs(atan(p.y, p.x))/PI),0),0).x*.25;
    if (amp > 1.)
        v=asin(sin(an*10.))/(PI*.5);

    return length(p)-r-amp*v*0.01+freq;
}

float sub(float a, float b)
{
    return max(a, -b);
}

vec3 rdr(vec2 uv)
{
    uv += vec2(asin(sin(-iTime*5.)), cos(iTime*5.))*.15;
    vec3 col = vec3(18.,22.,60.)/255.;
    vec3 red = vec3(252.,139.,111.)/255.;
    vec3 green = vec3(113.,197.,157.)/255.;

    float th = 0.008;
    float sp = 0.05;

    float r = .02;
    //float a = abs(cir(uv, r, 0.01))-th;
    //col = mix(col, vec3(1.), 1.-sat(a*400.));

    float a;
    int i = 0;
    float bps = 2.;
    float beat = mod(iTime, 1./bps)*bps;
    int cnt = int(mix(2., 8., mod(iTime, 10.)/10.));
    while (i < cnt)
    {
        float fi = float(i);
        r += sp+texelFetch(iChannel0, ivec2(i*5, 0.), 0).x*.05;
        a = abs(cir(uv, r, fi/2.5+.5*sin(iTime+fi)))-th;
        vec3 rgb = (mod(fi, 2.) < 0.1 ? red : green);
    	col = mix(col, rgb, 1.-sat(a*iResolution.x*.025));
        col += .5*rgb*(1.-sat(a*5.))*sat(pow(1.-beat, .5)+.2);
    	++i;
    }

    col = mix(col, 2.*col.zyx*vec3(sin(iTime)*.2+.8, uv*.5+.5), sin(iTime*.5));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    vec2 ouv = uv;
	uv *= 4.;

//    uv *= (sin(iTime*.2)*.2+.5)+.1*sin(iTime*.34);
    uv *= sin(iTime)*.5+1.;

    uv *= r2d(sin(iTime*.5)*.5+length(ouv));
    uv += vec2(sin(iTime), cos(iTime));

    float rep = mix(3.,.75, float(mod(iTime, 6.) < 3.));
    uv = mod(uv+rep*.5, rep)-rep*.5;
    vec3 col = rdr(uv/rep);
	vec3 a = col + vec3(.23,.54,.36)*1.0-abs(ouv.x*2.);
    vec3 b = col + vec3(.23,.54,.36).zxy*abs(ouv.x*2.);
    col = mix(a, b, sin(iTime)*.5+.5);
    col = pow(col, vec3(.45));
    fragColor = vec4(col.zyx,1.0);
}

// variant of "hexastairs"  https://shadertoy.com/view/3sGfWm
// using "hexa world" https://shadertoy.com/view/tsKBDD

#define H(I)   fract(1e4*sin(1e4*length(vec2(I))))         // cheap hash
//#define H(I) hash(uvec3(I.xy,0))                         // the one used in "hexa world": integer hash from https://www.shadertoy.com/view/XlXcW4
#define h(x,y) int( 4.* H( I + ivec3(x,y,0) ) )            // O-3 random int at relative cell(dx,dy)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 12.* u / R.y + iTime;                         // === std hexagonal tiling data

    U *= mat2(1,0,.5,.87);                                 // parallelogram frame
    vec3  V = vec3( U, U.y-U.x +3. );                      // 3 axial coords
    ivec3 I = ivec3(floor(V)), J;
          I += I.yzx;
          J = I % 3;                                       // J.xy = ~ hexagon face
    int   f = J.x==2 ? 1 : J.y==2 ? 2 : 0,                 // f: front face id 
          b = J.x==1 ? 1 : J.y==0 ? 2 : 0, k,o=0;          // b: back face id
    I.x += 4; I /= 3;                                      // I.xy = hexagon id
    V = mod( V + vec3( I.y, I.y+I.x, I.x ), 2. );          // local coords
    k = h(0,0);                                            // rand values per hexagon

                                                           // === custom hexatile pattern drawing
    if (k==3) {                                            // --- plain cubes (i.e. stairless)
        k = f+2;                                           // base color = face id
#define check(i,j,s, X,Y)  \
             if (h( i, j)==s && abs(X-1.5)<.25 && Y<.75)  k++
             check(-f,-1, 1-f,  V.x+float(f), V[2-f] );    // regular doors
        else check( 0, 1,  1,   V.y   , 2.-V.z );          // horiz doors
        else check( 1, 1,  0,   V.z   , 2.-V.y );         
        else check(-1, 0,  2,   V.y+1.,  V.x   );          // tilted doors
        else check( 1, 0,  2,   V.z+1., 2.-V.x );             
    }
    else {                                                 // --- cubes with stairs
        float s=1.,f=1.,l=1.;
        V = k==1 ? f=-f, l=0., V.yzx                       // apply random hexagon rotation
          : k==2 ? s=-s, V.yxz : V;
        s *= mod(8.*V.y+l,2.) - 1.;                        // stair striping
        l =  2.*V.x-V.y +(abs(s)-9.)/8.;                   // stair dented slope
        k =  f*( 2.*V.x-V.y-1.)>.5 || -f*l>.5 ? o=1, b+2   // draw rear faces. set o for ambiant occlusion
           : f*l > .3 ? k+2                                // draw stair sides
           :  s  < 0. ? k+1 : k;                           // draw stair steps
    }
    O = vec4(k%3)/2.;
    if (o>0) O *= min(1.,length(V-1.)*.8);                 // ambiant occlusion behind stairs
}

#define COLOR_RED   		vec3(1.0, 0.0, 0.0)
#define COLOR_GREEN 		vec3(0.0, 1.0, 0.0)
#define COLOR_BLUE  		vec3(0.0, 0.0, 1.0)
#define LINE_THICKNESS_MIN 	1.5
#define LINE_THICKNESS_MAX 	50.0
#define M_PI				3.141592654

float DistanceToLine(vec2 a, vec2 b, vec2 p)
{
    vec2 pos = p - a;
    vec2 dir = b - a;
    float len = length(dir);
    dir /= len;
    float f = dot(dir, pos);
    if (f < 0.0)
        return distance(a, p);
   	else if (f > len)
        return distance(b, p);
    else
        return distance(a + dir * f, p);
}

void DrawSmoothLine(inout vec3 fragColor, vec2 fragCoord, vec2 a, vec2 b, float thickness, vec3 color)
{
    float d = DistanceToLine(a, b, fragCoord);
    float t = clamp(thickness, LINE_THICKNESS_MIN, LINE_THICKNESS_MAX);
    float e = 1.0 + (t - LINE_THICKNESS_MIN) / (LINE_THICKNESS_MAX - LINE_THICKNESS_MIN) * (LINE_THICKNESS_MAX * 10.0);
    float s = pow(smoothstep(0.0, t, d), e);
    fragColor += color * (1.0 - s);
}

#define SMOOTH_LINE(A, B, THICKNESS, COL) \
    DrawSmoothLine(col, fragCoord, (A), (B), (THICKNESS), (COL))

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 col = vec3(0);
    float anim = 0.5 + 0.5 * clamp(cos(iTime + M_PI)*1.1, -1.0, +1.0);
    float thickness = mix(LINE_THICKNESS_MIN, LINE_THICKNESS_MAX, anim);

    SMOOTH_LINE(
        iResolution.xy/2.0 + vec2(-150.0, -150.0),
        iResolution.xy/2.0 + vec2(   0.0, +150.0),
        thickness,
        COLOR_RED
   	);
    SMOOTH_LINE(
        iResolution.xy/2.0 + vec2(   0.0, +150.0),
        iResolution.xy/2.0 + vec2(+150.0, -150.0),
        thickness,
        COLOR_GREEN
   	);
    SMOOTH_LINE(
        iResolution.xy/2.0 + vec2(-150.0, -150.0),
        iResolution.xy/2.0 + vec2(+150.0, -150.0),
        thickness,
        COLOR_BLUE
   	);

    fragColor = vec4(col, 1.0);
}

vec3 ro = vec3(0), rd = vec3(0);
vec3 col = vec3(0);
