// SDF functions
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float sdSphere(in vec3 p,in float r)
{
    return length(p)-r;
}

float sdCappedCylinder( vec3 p, vec2 h )
{
    vec2 d = abs(vec2(length(p.xz),p.y)) - h;
    return ((min(max(d.x,d.y),0.0) + length(max(d,0.0))));
}

float fOpUnionSmooth(float a,float b,float r)
{
    vec2 u = max(vec2(r - a,r - b), vec2(0));
    return max(r, min (a, b)) - length(u);
}
float vmax(vec3 v){
    return max(max(v.x, v.y), v.z);
}
float sdBox(vec3 p,vec3 b)
{
    vec3 d=abs(p)-b;
    return length(max(d,vec3(0)))+vmax(min(d,vec3(0.0)));
}
// a modified func of "fOpUnionColumns" by hg_sdf:
// https://mercury.sexy/hg_sdf/
vec4 v4OpUnionSink(vec4 a, vec4 b, float r, float n) {

    vec2 p = vec2(a.x, b.x);
    float rad = r*sqrt(2.)/(2.+sqrt(2.));
    p.x -= sqrt(2.)/2.*r;
    p.x += rad*sqrt(2.0);
    p.y -= rad;
    float d = length(p+vec2(0,n)) - rad;
    d = fOpUnionSmooth(d, b.x, p.x);
    d = min(d, a.x);
    return vec4(d, (d==a.x)?a.yzw:b.yzw);
}

