// scene: Shapes.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

#define MAT0 vec3(0.890,0.871,0.769)
#define MAT0_1 vec3(0.851,0.702,0.451)
#define MAT1 vec3(1.000,1.000,1.000)
#define MAT2 vec3(0.690,0.969,0.890)
vec4 sdScene(vec3 p)
{
    float d = MAX_DIST;
    vec4 res = vec4(MAX_DIST, MAT_VOID);
    //p.xz+=1.25;
    R(p.xz, iTime*.05);

    p.xz = abs(p.xz);
	d = sdSphere(p, 2.);
	res = vec4(d, MAT0);
    float anim = elasticInOut(s2u(sin(iTime*2.)))*.3;
    float anim2 = elasticInOut(s2u(sin(iTime*2.-PI)))*.3;
    vec3 q = p;
    d = sdCappedCylinder(rot(q+vec3(0., 0., -3.-anim), vec3(PI*.5, 0., 0.)), vec2(0.5, 1.1));
	res = v4OpUnionSink(vec4(d, MAT1), res, .7, 1.*anim);

	d = sdBox(q+vec3(-3.-anim2, 0., 0), vec3(1.1, 0.4000,0.4000));
    res = v4OpUnionSink(vec4(d, MAT1), res, .7, 1.*anim2);
    return res;
}

vec4 sdSticks(vec3 p)
{
    float d = MAX_DIST;
    vec4 res = vec4(MAX_DIST, MAT_VOID);
    //p.xz+=1.25;
    R(p.xz, iTime*.05);

    //p.xz = abs(p.xz);
    float anim = elasticInOut(s2u(sin(iTime*2.)))*.3;
    float anim2 = elasticInOut(s2u(sin(iTime*2.-PI)))*.3;
    vec3 q = p;
    d = sdCappedCylinder(rot(q+vec3(0., 0., -3.-anim), vec3(PI*.5, 0., 0.)), vec2(0.5, 1.1));
	res = v4OpUnionSink(vec4(d, MAT1), res, .7, 1.*anim);

	d = sdBox(q+vec3(-3.-anim2, 0., 0), vec3(1.1, 0.4000,0.4000));
    res = v4OpUnionSink(vec4(d, MAT1), res, .7, 1.*anim2);
    return res;
}


//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec4 intersect()
{
    float d = 1.;
    vec3  m = MAT_VOID;

    for (int i = ZERO; i < ITERATION; i++)
    {
        vec3 p = ro + d * rd;
        vec4 res = sdScene(p);
        m = res.yzw;
        res.x *= .5;
        if (abs(res.x) < MIN_DIST || res.x >= MAX_DIST) break;
        d += res.x;
        if (d >= MAX_DIST) break;
    }

    return vec4(d,m);
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
vec3 normal(vec3 p)
{
    float c=sdScene(p).x;
    float e=MIN_DIST*.1;
    return normalize(vec3(
        sdScene(p+vec3(e,0.,0.)).x-c,
        sdScene(p+vec3(0.,e,0.)).x-c,
        sdScene(p+vec3(0.,0.,e)).x-c)
    );
}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float shadow(vec3 o, vec3 n)
{
    float mint=L0_str;
    float maxt=L0_end;
    float k = L0_sft;
    float res = 1.;
    float t=mint;
    for( int i=0; i < ITERATION; i++)
    {
        float h = sdScene(o + L0_dir*t).x;
        res = min( res, k*h/t);
        t += h;
        if( res<0.001 || t>maxt ) break;
    }
    return clamp( res, 0.0, 1.0 );
}


vec3 randomSphereDir(vec2 rnd)
{
    float s = rnd.x*PI*2.;
    float t = rnd.y*2.-1.;
    return vec3(sin(s), cos(s), t) / sqrt(1.0 + t * t);
}
vec3 randomHemisphereDir(vec3 dir, float i)
{
    vec3 v = randomSphereDir( vec2(hash11(i+1.), hash11(i+2.)) );
    return v * sign(dot(v, dir));
}

// "Hemispherical SDF AO" by XT95:
// https://www.shadertoy.com/view/4sdGWN
float ambientOcclusion( in vec3 p, in vec3 n, in float maxDist, in float falloff )
{
    const int nbIte = 12;
    const float nbIteInv = 1./float(nbIte);
    const float rad = 1.-1.*nbIteInv; //Hemispherical factor (self occlusion correction)

    float ao = 0.0;

    for( int i=0; i<nbIte; i++ )
    {
        float l = hash11(float(i))*maxDist;
        vec3 aord = normalize(n+randomHemisphereDir(n, l )*rad)*l; // mix direction with the normal// for self occlusion problems!

        ao += (l - max(sdScene( p + aord ).x,0.)) / maxDist * falloff;
    }

    return clamp( 1.-ao*nbIteInv, 0., 1.);
}


//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
float specular(vec3 p, vec3 n, vec3 ld, float power)
{
    vec3 to_eye = normalize(p - ro);
    vec3 reflect_light = normalize(reflect(ld, n));
    return pow(max(dot(to_eye, reflect_light), 0.), power);
}

// https://www.shadertoy.com/view/wljSz1
vec4 sss(vec3 ro, vec3 rd){
    vec3 p = ro;
    float pres = .03;
    float e = 0.;
    for(int i = 0; i < 120; ++i){
        float d = sdScene(p).x;

        if(d > -MIN_DIST)
            break;

        e += -pres; // marching with constant. 
        p -= pres*rd; // marching with constant.
        //st_assert(i<59);
    }
    return vec4(p, e);
}

// for sss occlusion
vec4 absss(vec3 ro, vec3 rd){
    vec3 p = ro;
    float pres = .02;
    float e = 1.;
    vec4 d;
    for(int i = 0; i < 120; ++i){
        d = sdSticks(p);

        if(d.x < MIN_DIST)
            break;

        e *= 1.-pres; // marching with constant. 
        p += pres*rd; // marching with constant.
        rd=normalize(rd+u2s(hash33(p))*.05);
        //st_assert(i<59);
    }
    e = 1.-e*1.;
    return vec4(p, e);
}


// abailable: AMB_COL, AMB_STRENGTH, FOG_COL, FOG_START, L0_col, L0_dir, L0_str, L0_end, L0_sft
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void render()
{
    vec4 hit = intersect();
    vec3 p = ro + hit.x * rd;
    vec3 base_col = hit.yzw;

    if (hit.x>=MAX_DIST)
    {
        col=FOG_COL*(.65+.45*saturate(dot(-rd, L0_dir)));
    }
    else
    {
        vec3 n = normal(p);
        vec3 offset = n * .00001;
        float light1 = saturate(dot(n, L0_dir));
        float light2 = saturate(dot(n, L1_dir))*.3;


        float ao=0.;
        ao = ambientOcclusion(p, n, .1, 2.);
        ao += ambientOcclusion(p, n, .5, 2.);
        ao += ambientOcclusion(p, n, 1., 2.);
        ao += ambientOcclusion(p, n, 4., 2.);
        ao = smoothstep(0., 4., ao);

        float shade = 0.;
        shade = light1;
		float spec = 0.;
        vec3 shadeLight1 = vec3(L0_col*light1);
        vec3 shadeLight2 = vec3(L1_col*light2);
		if(distance(MAT0,base_col)<.01)
        {
            light1 = s2u(dot(n, L0_dir));
			L0_sft = 5.;
            float shadow = shadow(p+offset, n);
            // https://www.shadertoy.com/view/wljSz1
            vec3 h = normalize(mix(-n, rd, .5));
            // sv.zyz contains outgoing position, w contains accumulate distance (path "tightness")
            vec4 sv = sss(p+h, -L0_dir);
            // subsurface magic term
            float ss = saturate(-sv.w*.3);
            // light visibility across the volume
            float ssha = max(0., dot(normal(sv.xyz), normalize(p-sv.xyz)));
            float ssmix = mix(dot(n, L0_dir)*.5+.5, ss, .5);
            shadeLight1 = vec3(ssmix)*L0_col;
            vec4 ab = absss(p-n*.01, rd);
            float abss = saturate(ab.w);
            //st_assert(sss>.1);
            base_col = mix(MAT0, MAT0_1, mix(sms(.0, 1., ss), (1.-.05+.95*saturate(1.-abss)), .5));
            spec = specular(p+offset, n, L0_dir, 155.)*shadow*1.;
            col = shadeLight1;
            col *= .8+.2*shadow;
            col+= MAT0*(.5+.5*ao)*.3;
            col*= base_col;
            col = mix(col, col+L0_col, spec);
            //col = vec3(abss);
        }
        else
        {
            L0_sft = 50.;
            float shadow = shadow(p+offset, n);
            col = shadeLight1;
            col *= shadow;
            col+= shadeLight2*ao;
            col+= AMB_COL*ao*.4;
			col+= mix(MAT0, MAT0_1, .85)*sms(1.,.05,ao)*.75;
            col*= base_col;
            col = mix(col, col+L0_col, spec);
            col = mix(col, FOG_COL, saturate(pow(distance(ro,p)/MAX_DIST+FOG_START, FOG_POW)));
            //col = vec3(0);
        }

    }
}


