// "camera": create camera vectors.
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

vec3 quat_rotate(vec4 quat, vec3 dir)
{
    return dir + 2.0 * cross(quat.xyz, cross(quat.xyz, dir) + quat.w * dir);
}

void camera(vec2 uv)
{

    vec3 dir = normalize(vec3(0,-.4,1));//quat_rotate(Camera_quat, vec3(0,0,-1)).xzy;
    vec3 up = vec3(0,1,0);//quat_rotate(Camera_quat, vec3(0,1,0)).xzy;
    vec3 pos = vec3(0,11.75,-30);
    float fov = .14;
    vec3 target = pos-dir;

    vec3 cw = normalize(target - pos);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));

    mat3 camMat = mat3(cu, cv, cw);
    rd = normalize(camMat * normalize(vec3(sin(fov) * uv.x, sin(fov) * uv.y, -cos(fov))));
    ro = pos;

}

//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Setup UV...
    vec2 uv = fragCoord.xy / iResolution.xy;
    if(uv.x>INV_ERNST_RENDER_SCALE || uv.y>INV_ERNST_RENDER_SCALE)
        return;
    uv*=ERNST_RENDER_SCALE;
    float ml = (min(iResolution.x, iResolution.y)==iResolution.x)?1.0:iResolution.y/iResolution.x;
    uv = (uv*2.-1.)*ml;
    uv.x *= iResolution.x / iResolution.y;

    // Rendering...
    init();
    camera(uv);
    render();

    col*= .75+.25*pow(1.-length(uv), 1.);

    col = ACESFilm(col);

    fragColor = vec4(col, 1.);
}

common_code:
vec3 ACESFilm(vec3 x){
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

// General
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
#define iFrameRate 24.
#define ZERO min(0,iFrame)
#define PI 3.14159265
#define HALF_PI 1.5707963267948966
#define PI2 (2.0*PI)
#define PHI (sqrt(5.0)*0.5 + 0.5)
#define saturate(x) clamp(x, 0.0, 1.0)
#define sms(min, max, x) smoothstep(min, max, x)
#define s2u(x) (x*.5+.5)
#define u2s(x) ((x*2.)-1.)

// Random & Noise
//~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

// "Random number generator" by Toshiya Hachisuka:
// https://www.ci.i.u-tokyo.ac.jp/~hachisuka/tdf2015.pdf
float rnd(inout vec4 n){
    const vec4 q=vec4(1225.0,1585.0,2457.0,2098.0);
    const vec4 r=vec4(1112.0,367.0,92.0,265.0);
    const vec4 a=vec4(3423.0,2646.0,1707.0,1999.0);
    const vec4 m=vec4(4194287.0,4194277.0,4194191.0,4194167.0);
    vec4 beta=floor(n/q);
    vec4 p=a*(n-beta*q)-beta*r;
    beta=(sign(-p)+vec4(1.0))*vec4(0.5)*m;
    n=(p+beta);
    return fract(dot(n/m,vec4(1.0,-1.0,1.0,-1.0)));
}

// Hash / Noise by Anonymous:
// I forgot where I found this...
float hash( float n )
{
    return fract(sin(n)*158.5453);
}
float noise( in float x )
{
    float p = floor(x);
    float f = fract(x);
    f = f*f*(3.0-2.0*f);
    return mix( hash(p+0.0), hash(p+1.0),f);
}

// "Integer Hash - II" by iq:
// https://www.shadertoy.com/view/XlXcW4
  const uint k = 1103515245U;  // GLIB C
//const uint k = 134775813U;   // Delphi and Turbo Pascal
//const uint k = 20170906U;    // Today's date (use three days ago's dateif you want a prime)
//const uint k = 1664525U;     // Numerical Recipes
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}


float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

