void readFrameTexCamera3x10(sampler2D tex, int id, int f, inout vec3 pos, inout vec4 quat, inout float fov, in vec3 tra_scl, in vec4 quat_scl)
{
    vec4 anm = texelFetch(tex, ivec2(f, id),0);
    pos = unpack_bl(anm.x) * tra_scl;

    quat = vec4(0);
    quat.xyz = unpack_bl(anm.y)*quat_scl.xyz;
    vec3 qw_fov_zero = unpack_bl(anm.z);
    quat.w = qw_fov_zero.x*quat_scl.w;

