}

void readFrameTex3x10(sampler2D tex, int id, int f, inout vec3 pos, inout vec3 rot, in vec3 tra_scl)
{
    vec4 anm = texelFetch(tex, ivec2(f, id),0);
    pos = unpack_bl(anm.x) * tra_scl;
    rot = unpack_bl(anm.y).xyz * PI;
}

void readFrameTexLight3x10(sampler2D tex, int id, int f, inout vec3 dir, inout vec3 col, inout float clip_start, inout float clip_end, inout float softness, in vec3 shadow_props_scl)
{
    vec4 anm = texelFetch(tex, ivec2(f, id),0);
    dir = unpack_bl(anm.x).xzy;
    col = unpack_bl(anm.y);
    vec3 shadow = unpack_bl(anm.z);
    clip_start = shadow.x*shadow_props_scl.x;
    clip_end = shadow.y*shadow_props_scl.y;
    softness = shadow.z*shadow_props_scl.z;
}


