//--- Round Brilliant Cut
// by Catzpaw 2020

//BufferA:scene

//refraction index
#define RI1 2.40
#define RI2 2.44

//settings:trace
#define ITER   256
#define EPS   1e-4
#define NEAR   1.0
#define FAR   10.0

//settings:trace2
#define ITER2  128
#define EPS2  1e-4
#define NEAR2 0.02

//CONSTANTS
const float pi=acos(-1.);
const float qp=pi*.25,op=qp*.5,hp=op*.5;
const mat2 rot4=mat2(sin(qp),cos(qp),-cos(qp),sin(qp));
const mat2 rot3=mat2(sin(qp+op),cos(qp+op),-cos(qp+op),sin(qp+op));
const mat2 rot2=mat2(sin(op),cos(op),-cos(op),sin(op));
const mat2 rot1=mat2(sin(hp),cos(hp),-cos(hp),sin(hp));

//SDF
float sdTable(vec3 p){
	vec2 d=abs(vec2(length(p.xz),(p.y+0.159)*1.650))-vec2(1,1);
	return min(max(d.x,d.y),0.)+length(max(d,0.));
}
float sdCut(vec3 p,float a,float h){
    p.y*=a;
    p.y-=(abs(p.x)+abs(p.z))*h;p=abs(p);
	return (p.x+p.y+p.z-1.)*.5;
}

//SCENE
float map(vec3 p){
    p.yz*=rot(.3);
    p.xz*=rot(-iTime*.8);
    //table & girdle
    float d=sdTable(p);
    //star facet
    vec3 q=p*0.3000;q.y+=0.0808;q.xz*=rot2;
	q.xz=abs(q.xz);q.xz*=rot4;q.xz=abs(q.xz);
    q.xz*=rot2;d=max(d,sdCut(q,3.700,0.0000));
    //main facet
    q=p*0.691;
    q.xz=abs(q.xz);q.xz*=rot4;q.xz=abs(q.xz);
    q.xz*=rot2;d=max(d,sdCut(q,1.868,0.1744));
    //girdle facet
    q*=1.022;q.y-=0.034;
    q.xz*=rot1;d=max(d,sdCut(q,1.650,0.1000));
    q.xz*=rot3;d=max(d,sdCut(q,1.650,0.1000));
	return d;
}

//NORMAL
vec3 normal(vec3 p){
    const vec2 e=vec2(-1,1);
    return normalize(e.xxx*map(p+e.xxx*EPS)+e.xyy*map(p+e.xyy*EPS)+
                     e.yxy*map(p+e.yxy*EPS)+e.yyx*map(p+e.yyx*EPS));
}

//RAYMARCHING
float trace(vec3 ro,vec3 rd,out vec3 p,out vec3 n){
    float t=NEAR,d;
    for(int i=ZERO;i<ITER;i++){
        p=ro+rd*t;
        d=map(p);
        if(abs(d)<EPS||t>FAR)break;
        t+=step(d,1.)*d*.5+d*.5;
    }
    n=normal(p);
    return min(t,FAR);
}

//RAYMARCHING UNDER SURFACE
float trace2(vec3 ro,vec3 rd,out vec3 p,out vec3 n){
    float t=NEAR2,d;
    for(int i=ZERO;i<ITER2;i++){
        p=ro+rd*t;
        d=-map(p);
        if(abs(d)<EPS2||d<EPS2)break;
        t+=d;
    }
    n=-normal(p);
    return t;
}

//CUBEMAP
vec3 cubemap(vec3 d){    
	return texture(iChannel0,d).rgb;
}

//FRESNEL EQUATION
float schlickFresnel(float ri,float co) {
    float r=(1.-ri)/(1.+ri);
    r=r*r;
    return r+(1.-r)*pow(1.-co,5.);
}

//GET LIGHT PATH
vec3 lightPath(vec3 p,vec3 rd,float ri){
    vec3 n;
    vec3 r0=-rd;
    //2nd reflection
    trace2(p,rd,p,n);
    rd=reflect(rd,n);
    vec3 r1=refract(rd,n,ri);
    r1=length(r1)<EPS?r0:r1;
    //3rd reflection
    trace2(p,rd,p,n);
    rd=reflect(rd,n);
    vec3 r2=refract(rd,n,ri);
    r2=length(r2)<EPS?r1:r2;
    //final refraction
    trace2(p,rd,p,n);
    vec3 r3=refract(rd,n,ri);
    return length(r3)<EPS?r2:r3;
}

//MATERIAL
vec3 material(vec3 p,vec3 rd,vec3 n){

    //1st reflection
    vec3 l0=reflect(rd,n);

    //cosine for fresnel
    float co=max(0.,dot(-rd,n));

    //low frequency light path
    float f1=schlickFresnel(RI1,co);
    vec3 l1=lightPath(p,refract(rd,n,1./RI1),RI1);

    //high frequency light path
    float f2=schlickFresnel(RI2,co);
    vec3 l2=lightPath(p,refract(rd,n,1./RI2),RI2);

    //dispersion
    float a=0.;
    vec3 dc=vec3(0);
    vec3 r=cubemap(l0);
    for(int i=ZERO;i<10;i++){
        vec3 l=normalize(mix(l1,l2,a));
        float f=mix(f1,f2,a);
        dc+=cubemap(l)*hsv(a+.9,1.,1.)*(1.-f)+r*f;
        a+=.1;
    }
    dc*=.19;

    return dc;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //CAMERA SETUP
    vec2 m=iMouse.xy/iResolution.xy;
    vec2 uv=(fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 p,n,c=vec3(0),
         ro=vec3(0,0,-3.5),
         rd=normalize(vec3(uv,1.5));
    mat2 r=rot(sin(iTime*.3+4.)*.5-.8+m.y*6.3);
    ro.yz*=r;
    rd.yz*=r;
    r=rot(iTime*.21+.7+m.x*6.3);
    ro.xz*=r;
    rd.xz*=r;

    //GET SURFACE
    float t=trace(ro,rd,p,n);

    //SHADING
    float w=0.;
    if(t>9.){
        //background
        c=cubemap(rd);
    }else{
        //diamond
	    c=material(p,rd,n);
        w=smoothstep(1.60,1.61,length(c));
    }
