void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = ( 2. * fragCoord.xy - iResolution.xy) / iResolution.y;
	uv *= 1.; //zoom

    //create and offset tiles
	float hh = 0.895;
	uv.x += step(1.,mod(uv.y,2.)) *(hh/2.);
	uv.x  = mod(uv.x,hh);
	uv.y  = mod(uv.y,1.)-0.05;

	float ofs = 0.12;                //center offset
	float len = hh * 0.516 - ofs;    //length size
    float div = 12.;                 //division
    float lnw = 0.0051;              //line weight
	float ang;
	float sec;
	vec3  lin;
    vec3  col;
	float mi = 1.; 

    for(float m = 0.; m < 2.; m++){        //mirror x coordinate
        if(m == 1.){uv.x = hh - uv.x;}
        for(float i = 0.; i < div; i++){   //radial array
            sec = 6.28318/div;
            ang = sec*i+sec/2.;

            vec2 a = pcart(ofs,ang); a += vec2(hh/2.,0.5);

            vec2 b = a + pcart(len*1.000,ang + sec*1.50);
            vec2 c = b + pcart(len*0.505,ang + sec*4.05);
            vec2 d = c + pcart(len*0.500,ang + sec*1.10);

            //deform d
            if (i==7.) {d = c + pcart(len/2.,ang + sec*2.15);}
            if (i==6.) {d = c + pcart(len/2.,ang + sec*0.86);}

            vec2 e = d + pcart(len*0.500,ang + sec*2.90);
            vec2 f = a + pcart(len+0.050,ang);
            vec2 g = f + pcart(len*0.518,ang + sec*2.14);

            //deform g
            if (i==1.||i==7.0){g = f + pcart(len*0.51,ang + sec*2.1);}
            if (i==2.||i==8.0){g = f + pcart(len*0.50,ang + sec*1.1);}

            // minimum sdf
            mi = min(mi, sdLine(uv,a,b));
            mi = min(mi, sdLine(uv,b,c));
            mi = min(mi, sdLine(uv,c,d));
            mi = min(mi, sdLine(uv,d,e));
            mi = min(mi, sdLine(uv,f,g));
        }
    }

    col  = vec3(1.0) ;
    col *= 0.3 + 0.4*smoothstep(mi,0.02,0.00);
    col *= 1.0 + 0.2*smoothstep(mi,0.03,0.00);
    col  = mix(col,vec3(1.0),1.0-smoothstep(0.0,lnw,abs(mi)));

	fragColor = vec4(col, 1.0);
}

