                0., cos(radAngle), -sin(radAngle),
                0., sin(radAngle), cos(radAngle));
}

mat3 yRotationMatrix(float radAngle)
{
    return mat3(cos(radAngle),  0., sin(radAngle),
                0.,             1., 0.,
                -sin(radAngle), 0., cos(radAngle));
}

mat3 zRotationMatrix(float radAngle)
{
    return mat3(cos(radAngle), -sin(radAngle), 0.,
                sin(radAngle),  cos(radAngle), 0.,
                0., 0., 1.);
}

mat4 transformationMatrix(vec3 translation, vec3 radAngles)
{
    mat3 xMatrix = xRotationMatrix(radAngles.x);
    mat3 yMatrix = yRotationMatrix(radAngles.y);
    mat3 zMatrix = zRotationMatrix(radAngles.z);

    mat3 rotationMatrix = zMatrix * yMatrix * xMatrix;
    mat4 result = mat4(rotationMatrix[0].x, rotationMatrix[1].x, rotationMatrix[2].x, translation.x,
                       rotationMatrix[0].y, rotationMatrix[1].y, rotationMatrix[2].y, translation.y,
                       rotationMatrix[0].z, rotationMatrix[1].z, rotationMatrix[2].z, translation.z,
                       0.,				    0., 			     0.,                  1.);
    return result;
}

mat4 transformationMatrix(vec3 translation, vec3 radAngles, float scale)
{
    mat3 xMatrix = xRotationMatrix(radAngles.x);
    mat3 yMatrix = yRotationMatrix(radAngles.y);
    mat3 zMatrix = zRotationMatrix(radAngles.z);

    mat3 rotationMatrix = zMatrix * yMatrix * xMatrix;
    mat4 result = mat4(rotationMatrix[0].x, rotationMatrix[1].x, rotationMatrix[2].x, translation.x,
                       rotationMatrix[0].y, rotationMatrix[1].y, rotationMatrix[2].y, translation.y,
                       rotationMatrix[0].z, rotationMatrix[1].z, rotationMatrix[2].z, translation.z,
                       0.,				    0., 			     0.,                  scale);
    return result;
}

