#define PI 3.1415926538
#define DEG2RAD(deg) deg * PI / 180.

#define MAX_STEPS 800
#define STEP_SIZE 0.01
#define MAX_DIST 10.
#define EPSILON 0.00001

// Camera strongly based on https://www.scratchapixel.com/lessons/3d-basic-rendering/ray-tracing-generating-camera-rays/generating-camera-rays
// and http://viclw17.github.io/2018/11/29/raymarching-algorithm/
struct Camera 
{
    vec3 origin;
    vec3 rayDirection;
};

Camera buildCamera(vec2 uv, float imageRatio, float radFieldOfView, vec3 eyePosition, vec3 translation, vec3 rotation)
{
    vec2 screenPixel = vec2(uv) * 2. - vec2(1.);
    screenPixel *= tan(radFieldOfView / 2.);
    screenPixel.x *= imageRatio;

    mat4 worldToCamMatrix = transformationMatrix(translation, rotation);

    vec3 pixelPosition = (vec4(vec3(screenPixel, 1), 1.)* worldToCamMatrix).xyz;

    vec3 rayDirection = (vec4(normalize(pixelPosition - eyePosition), 1.) * worldToCamMatrix).xyz;

    return Camera(pixelPosition, rayDirection);
}    


float sdfTorus(vec3 point, vec2 size)
{
  	vec2 q = vec2(length(point.xz)- size.x, point.y);
	return length(q) - size.y;
}


vec4 skyShading(vec3 direction)
{
	return vec4(vec3(abs(sin(direction) * cos(direction))), 1.);
}

// https://iquilezles.org/articles/distfunctions
float displacement(vec3 p)
{
	return sin((1.25 * sin(iTime)) * p.x) * sin((1.25 * cos(iTime)) * p.y) * sin((1.25 * sin(iTime)) * p.z);
}

float opDisplace(vec3 p)
{
    float d1 = sdfTorus(p, vec2(2., 0.5));
    float d2 = displacement(p);
    return d1 + d2;
}

//http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        opDisplace(vec3(p.x + EPSILON, p.y, p.z)) - opDisplace(vec3(p.x - EPSILON, p.y, p.z)),
        opDisplace(vec3(p.x, p.y + EPSILON, p.z)) - opDisplace(vec3(p.x, p.y - EPSILON, p.z)),
        opDisplace(vec3(p.x, p.y, p.z  + EPSILON)) - opDisplace(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

//https://en.wikipedia.org/wiki/Phong_reflection_model
vec4 phong(vec3 point, Camera camera)
{
	vec3 normal = estimateNormal(point);
    const vec3 specular = vec3(1., 1., 1.);
    const vec3 diffuse = vec3(1., 1., 1.);
    const float ambient = 0.5;
    const float shininess = 10.;

    const vec3 light = vec3(1., 1., 2.);
    vec3 directionLightVector = normalize(light - point);
    vec3 perfectRay = 2. * (directionLightVector * normal) * normal - directionLightVector;
    vec3 directionToViewer = normalize(camera.origin - point);

    // The actual phong formula "messing"
    vec3 color = ambient + (diffuse * (directionLightVector * normal) + specular * pow(abs(perfectRay * directionToViewer), vec3(shininess)));

    return vec4(color, 1.);
}

vec4 rayMarch(Camera cam)
{
    mat4 transform = transformationMatrix(vec3(0., 0., 0.), 
                                          vec3(DEG2RAD(180. * sin(iTime)), DEG2RAD(180. * sin(iTime)), DEG2RAD(180. * sin(iTime))), 
                                          1.);
   	float fullDistance = 0.;
    vec3 position = vec3(0., 0., -5);
    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 currentPoint = cam.origin + fullDistance * cam.rayDirection;

        bool hit = false;
        float accumulation = 0.;
        float currentDistance = opDisplace((transform * vec4(currentPoint + position, 1.)).xyz);
        if(currentDistance < EPSILON)
        {
            return phong((transform * vec4(currentPoint + position, 1.)).xyz, cam);
        }

        fullDistance += STEP_SIZE;
        if(fullDistance > MAX_DIST)
            return skyShading(cam.rayDirection);    	
    }

    return skyShading(cam.rayDirection);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    const float fieldOfView = 90.;
    vec2 uv = fragCoord / iResolution.xy;
    float imageRatio = iResolution.x / iResolution.y;
    Camera camera = buildCamera(uv, imageRatio, DEG2RAD(fieldOfView), vec3(0., 0., -2.), 
                                vec3(0., 0., 0.), vec3(0., 0., DEG2RAD(90. * cos(iTime))));


    fragColor = rayMarch(camera);
}

