void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Starting values:
    if (ivec2(fragCoord) == ivec2(0, 0)) {
        fragColor = vec4(0.0, 1.0, 0.0, 1.0);
    }

    if (ivec2(fragCoord) == ivec2(1, 0)) {
        fragColor = vec4(0.1, 0.6, 0.1, 1.0);
    }

    if (iFrame > 0) {
        // Update values (after initializing them):
        vec3 ballPos = getBallPos();
        vec3 ballVel = getBallVel();

        float distToEnv = mapEnvironment(ballPos);
        if (distToEnv < BALL_RADIUS) {
            vec3 hitNormal = getNormal(ballPos);
            ballVel = reflect(ballVel, hitNormal) * DECAY;
        }

        ballPos.y = max(sampleHeightmap(ballPos.xz) + BALL_RADIUS, ballPos.y);

        ballVel.y -= GRAVITY;
        ballPos += ballVel;
        if (ivec2(fragCoord) == ivec2(0, 0)) {
            fragColor = vec4(ballPos, 1.0);
        }

        if (ivec2(fragCoord) == ivec2(1, 0)) {
            fragColor = vec4(ballVel, 1.0);
        }
    }
}

