// Variable retrieval functions:
#define getBallPos() texelFetch(iChannel0, ivec2(0, 0), 0).xyz
#define getBallVel() texelFetch(iChannel0, ivec2(1, 0), 0).xyz

#define BALL_RADIUS 1.0

#define GRAVITY 0.01
#define DECAY 1.0

// Not exact:
float noise1(in float x) {
    return fract(sin(x * 12.527) * 573.173);
}

float noise2(in float x) {
    return fract(sin(x * 13.753) * 593.123);
}

float continuousNoise1(in float x) {
    const float r = 1.1;

    float x1 = floor(x * r) / r;
    float y1 = noise1(x1);

    float x2 = ceil(x * r) / r;
    float y2 = noise1(x2);

    return y1 + (0.5 + 0.5 * sin((2.0 * (x - x1) / (x2 - x1) - 1.0) * 1.57)) * (y2 - y1);
}

float continuousNoise2(in float x) {
    const float r = 1.1;

    float x1 = floor(x * r) / r;
    float y1 = noise2(x1);

    float x2 = ceil(x * r) / r;
    float y2 = noise2(x2);

    return y1 + (0.5 + 0.5 * sin((2.0 * (x - x1) / (x2 - x1) - 1.0) * 1.57)) * (y2 - y1);
}

float sampleHeightmap(in vec2 p) {
    vec2 sp = p * 0.5;
    float height = 2.0 * (continuousNoise1(sp.x + 0.5 * continuousNoise2(sp.y)) + continuousNoise2(sp.y + continuousNoise1(sp.x)));
    return height;
}

float mapCompoundTerrain(in vec3 p) {
    return p.y - sampleHeightmap(p.xz);
}

float mapCompoundWalls(in vec3 p) {
    vec3 q = abs(p);

    float outer = max(q.y - 15.0, max(q.x, q.z) - 30.0);
    float inner = max(q.y - 1000.1, max(q.x, q.z) - 29.9);

    return max(outer, -inner);
}

float mapEnvironment(in vec3 p) {
    return min(mapCompoundTerrain(p), mapCompoundWalls(p));
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapEnvironment(p + vec3(0.001, 0.0, 0.0)) - mapEnvironment(p - vec3(0.001, 0.0, 0.0)),
                          mapEnvironment(p + vec3(0.0, 0.001, 0.0)) - mapEnvironment(p - vec3(0.0, 0.001, 0.0)),
                          mapEnvironment(p + vec3(0.0, 0.0, 0.001)) - mapEnvironment(p - vec3(0.0, 0.0, 0.001))));
}

