float _cir(vec2 uv, float r)
{
    return length(uv)-r;
}

float stars(vec2 uv)
{
    uv *= r2d(.5);
    vec2 rep = vec2(0.1,0.1);
    ivec2 idx = ivec2((uv+.5*rep)/rep);

    float sz = (sin(float(idx.x)*3.3+float(idx.y)*15.)*.5+.5)*0.007;

    vec2 perturb = (rep*.5-sz)*vec2(sin(float(idx.x)), cos(float(idx.x)));

    vec2 p = perturb+mod(uv+rep*.5, vec2(rep))-rep*.5;

    float v = 1.-sat(_cir(p, sz)*400.);
    return v;
}



vec3 rdr(vec2 uv)
{
    float shp = 200.;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col = vec3(1.)*stars(uv);

    vec3 lightGreen = vec3(69, 181, 157)/255.;
    vec3 darkGreen = lightGreen * .6;
    vec3 pink = vec3(255, 0, 153)/255.;

    float plSz = .2;
    float planet = _cir(uv, plSz);
    float planetShadow = max(planet, _cir(uv-vec2(.025), plSz));

    vec3 planetCol = mix(lightGreen, darkGreen, sat(planetShadow*shp)*sat((sin((uv.y-uv.x)*200.)+.8)*shp));
    col = mix(col, planetCol,  1.0-sat(planet*shp));

    float rgSz = 0.5;
    float rgTh = 0.02;

    float tRgMv = iTime*2.;
    vec2 rgMv = vec2(sin(tRgMv)*0.025, cos(tRgMv)*0.01);

    float rgAn = sin(iTime)*PI/6.0;

    vec2 rPos = (uv*r2d(rgAn)-vec2(0.,-.05));
    float ringA = max(_cir(rPos*vec2(1.,4.), rgSz), -_cir(1.25*rPos*vec2(1.,4.)-vec2(0.,.157)+rgMv, rgSz));
    vec3 rCol = mix(vec3(0.1), pink,sat((abs(ringA)-rgTh)*shp));;
    col = mix(col, rCol, 1.-sat(ringA*shp));

    vec2 rPosB = (uv*r2d(-rgAn)-vec2(0.,-.05));
    float ringB = max(_cir(rPosB*vec2(1.,4.), rgSz), -_cir(1.25*rPosB*vec2(1.,4.)-vec2(0.,.157)+rgMv, rgSz));
    vec3 rColB = mix(vec3(0.1), pink,sat((abs(ringB)-rgTh)*shp));;
    col = mix(col, rColB, 1.-sat(ringB*shp));

	col += .3*sat(.5+.5*rand(uv*sin(iTime)));
    col *= .3+rand(uv*sin(iTime));

    return col;
}

vec3 rdrChroma(vec2 uv)
{
    uv *= pow(1.+length(uv),.5);
    vec3 col;
    vec2 dir = vec2(1.);
    float strength = 0.003+0.003*length(uv*2.);
    col.r = rdr(uv+dir*strength).r;
    col.g = rdr(uv).g;
    col.b = rdr(uv-dir*strength).b; 
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(0.5)*iResolution.xy)/iResolution.xx;
	uv *= 1.+sin(iTime)*.2+.5;
    vec3 col = rdrChroma(uv);

    fragColor = vec4(col,1.0);
}

