#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))
#define multiplier 9.0
#define iterations 2

const int MAX_MARCHING_STEPS = 1000;
const float MIN_DIST = 0.0,
MAX_DIST = 1000.0,
EPSILON = 0.001,
size = 1000.0;


const float scale = EPSILON*10.0; //to prevent rendering artifacts

float vines(vec3 p,float size1){
    //p /= 2.0;
    return size-length((sin(p/vec3(length(sin(p)))))*size1)/1.8;
}


float sceneSDF(vec3 p) {
    p /= scale*120.0;

    float
        size1 = 1100.0,
    	result = vines(p,size1),
    	i = multiplier;
    for(int j = 0; j < iterations; j++){
        //p = sin(p/i)*i-sin(p*i)/i;
        result = min(result,vines(p,size1)/(i));
        i *= multiplier;
    }
    return result*scale;
}

