const float PI = 3.14159265359;
float gMyTime  = 0.0;

void mainImage( out vec4 fragColor, in vec2 XY )
{
    gMyTime = (iTime * PI * 2.0);

    vec2 cp = iResolution.xy * (vec2(cos(gMyTime * 0.1), sin(gMyTime * 0.13)) * 0.14 + 0.5);

    if (length(iMouse.xy) > 50.0) {
        cp = iMouse.xy;
    }


    const float contribBias =  20.0;
    const float steps       =  50.0;

    vec2  p        = XY;
    vec2  vStep    = (cp - XY) / steps;
    vec2  vStepp   = normalize(vec2(-vStep.y, vStep.x));
    vec2  uStepp   = vStepp / vec2(iResolution.x, iResolution.y);
    float stepDist = length(vStep);
    vec3  accum    = vec3(0.0);
    float total    = 0.0;
    float dist     = 0.0;
    for (float step = 0.0; step < steps; step += 1.0) {
        float contrib  = contribBias / (contribBias + dist);
	    vec2 uv = p/vec2(iResolution.x, iResolution.y);

        float spreadMax = min(8.0, dist * 0.004);
        for (float spread = -spreadMax; spread <= spreadMax; spread += 1.0) {
            vec2 uv2 = uv + spread * uStepp * 11.3;
            if (sin(gMyTime * 0.1) < 0.0) {                
            	accum     += contrib * textureLod(iChannel0, uv2, min(2.0, dist * 0.1)).rgb;
            }
            else {
            	accum     += contrib * textureLod(iChannel1, uv2, min(2.0, dist * 0.1)).rgb;
            }
            total         += contrib;
        }
        p             += vStep;
        dist          += stepDist;
    }

    accum /= total;


    fragColor = vec4(accum, 1.0);
}

