    float t = 3.142*(.5-.5*cos(iTime)), v,
          s = 1.52,
          z = .387, w = s/2.-z, a = .718, x = .5/tan(a), y = 1.-x;
    O-=O;
#define P(I,J,K)  O += S( sdQ( U, vec2(0), I, J, I+K ) )

    U *= rot(t);
