#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec4 ra;
  vec3 q;
  float dMin, d, ds1, ds2, ds3;
  ra = vec4 (-0.08, -0.012, 0.37, -0.42) * pi;
  p.y -= carPos.y;
  p.xz = Rot2D (p.xz, carRot);
  p.z -= wlBase.z;
  dMin = dstFar;
  q = p;
  ds1 = max (Rot2D (q.yz, ra.z).y - 1.03, - Rot2D (q.yz, ra.w).y - 0.75);
  ds2 = Rot2D (abs (vec2 (q.x, q.y + 0.18)), ra.x).x - 1.3;
  ds3 = max (Rot2D (vec2 (q.y, abs (q.z)), ra.y).y - 3., ds1);
  d = max (max (abs (q.y - 0.1) - 1., ds2), ds3);
  d = max (d, - PrBoxDf (p - vec3 (0., 0.2, -2.), vec3 (1.08, 0.4, 0.9)));
  d = max (d, - PrCylDf (((vec3 (abs (q.xz) - wlBase.xz, q.y + wlRad + 0.35)).xzy).yzx,
     wlRad + 0.06, 0.5));
  d = max (d, - max (max (max (abs (q.y - 0.48) - 0.35, abs (q.z - 0.5) - 1.5), ds1 + 0.05),
     0.08 - abs (q.z - 0.5)));
  d = max (d, - max (ds2 + 0.08, min (max (abs (q.y - 0.68) - 0.2, q.z - 0.5),
     max (abs (q.y - 0.55) - 0.31, 0.5 - q.z))));
  d = max (d, - max (max (max (max (abs (q.y - 0.2) - 0.9, ds2), ds3) + 0.06,
     abs (q.z - 0.3) - 1.3), - max (max (length (vec2 (q.y + wlRad + 0.35, q.z - wlBase.z)) -
     (wlRad + 0.06 + 0.02), q.z - wlBase.z), - q.y - wlRad - 0.35)));
  DMINQ (idBod);
  q = p;
  q.z = abs (q.z);
  q.yz -= vec2 (- wlRad - 0.35, wlBase.z);
  d = PrCylDf (q.yzx, 0.08, wlBase.x);
  DMINQ (idAx);
  q = p;
  q.y -= -0.88;
  d = PrBoxDf (q, vec3 (1.3, 0.03, 1.35));
  DMINQ (idBas);
  q = p;
  q = vec3 (abs (q.xz) - wlBase.xz, q.y + wlRad + 0.35).xzy;
  if (p.z > 0.) q.xz = Rot2D (q.xz, - strRot * sign (p.x));
  q.yz = Rot2D (q.yz, - ((p.x < 0.) ? wlRot.x : wlRot.y) * sign (p.z));
  d = max (PrRoundCylDf (q.yzx, wlRad - 0.1, 0.1, 0.14), wlRad - 0.25 - length (q.yz));
  d = min (d, PrCylDf (q.yzx, wlRad - 0.25, 0.2));
  d = min (d, PrCylDf (vec3 (q.x, ((abs (q.y) < abs (q.z)) ? q.yz : q.zy)) - vec3 (0.17, 0., 0.),
     0.07, wlRad - 0.2));
  DMINQ (idWhl);
  q = p;
  q.yz = Rot2D (q.yz - vec2 (0.72, 0.2), -0.1 * pi);
  q -= vec3 (-0.5, -0.3, 1.5);
  d = PrRoundCylDf (q, 0.03, 0.03, 0.35);
  q.z -= -0.35;
  q.xy = Rot2D (q.xy, -8. * strRot + pi / 6.);
  q.xy = Rot2D (q.xy, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  d = min (d, PrTorusDf (q, 0.025, 0.35));
  q.x += 0.17;
  d = min (d, PrCylDf (q.yzx, 0.02, 0.17));
  DMINQ (idStr);
  q = p;
  q.yz -= vec2 (-0.4, 0.5);
  d = PrBoxDf (q, vec3 (1., 0.1, 0.35));
  q.yz = Rot2D (q.yz - vec2 (0.4, -0.43), 0.1 * pi);
  d = min (d, PrBoxDf (q, vec3 (1., 0.35, 0.05)));
  DMINQ (idSeat);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    dHit += d;
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
