       col4 = vec4 (1., 0., 0., -1.);
    else if (qHit.z > 2.98 && PrRoundBox2Df (qHit.xy + vec2 (0., 0.25), vec2 (1.2, 0.), 0.05) < 0.)
       col4 = vec4 (1., 1., 0.8, -1.);
    else if (qHit.z > 2.8 && PrRoundBox2Df (qHit.xy + vec2 (0., 0.5), vec2 (0.78, 0.15), 0.01) < 0.)
       s = 0.7 + 0.3 * step (0.2, cos (8. * pi * qHit.x));
    else if (qHit.y > -0.5 && abs (qHit.x) < 1.05 && abs (qHit.z + 2.) < 0.85)
       s = 1. - 0.1 * cos (16. * pi * qHit.x);
    else s = 0.6 + 0.4 * step (-0.75, qHit.y) * step (0., max (abs (qHit.y) - 0.85,
       abs (qHit.z - 0.5) - 0.01)) *
       step (0., max (abs (qHit.y + 0.09) - 0.3, abs (qHit.z - 2.) - 0.01)) *
       step (0., max (abs (qHit.y + 0.15) - 0.6, abs (qHit.z + 1.) - 0.01));
    s *= 0.5 + 0.5 * step (wlRad + 0.07, length (vec2 (qHit.y + wlRad + 0.35,
       abs (qHit.z) - wlBase.z)));
    col4.rgb *= s;
  } else if (idObj == idAx) {
    col4 = vec4 (0.3, 0.3, 0.4, 0.1);
  } else if (idObj == idBas) {
    col4.rgb *= 0.4;
  } else if (idObj == idWhl) {
    r = wlRad - length (qHit.yz);
    if (r < 0.22) col4 = vec4 (0.3, 0.3, 0.3, 0.) * (1. - 0.5 * step (r, 0.07) *
       abs (step (0., cos (32. * pi * qHit.x)) - step (0.5, mod (32. * atan (qHit.z, - qHit.y) /
       (2. * pi) + 0.5, 1.))));
    else {
      if (qHit.x > 0.205) col4 = vec4 (0.9, 0.9, 1., 0.2);
      else if (qHit.x < 0.) col4 *= 0.5;
    }
  } else if (idObj == idStr) {
    col4 = vec4 (0.9, 0.9, 0.9, 0.2);
  } else if (idObj == idSeat) {
    col4 = vec4 (0.9, 0.7, 0.4, 0.05) * (0.95 + 0.05 * cos (64. * qHit.x));
  }
  refFac = 0.;
  if (col4 == colB4 && (qHit.y < -0.5 || PrRoundBox2Df (qHit.xz + vec2 (0., 2.),
     vec2 (1.1, 0.95), 0.01) > 0.)) refFac = 0.12;
  return col4;
}

vec3 GrndCol (vec3 ro, float dstGrnd, float sh)
{
  vec3 col, colG, vn;
  vec2 vf, q;
  vec2 gId;
  float a, f, ff;
  gId = PixToHex (ro.xz / hgSize);
  q = Rot2D ((ro.xz - HexToPix (gId * hgSize)) / hgSize,
     (2. * step (Hashfv2 (gId), 0.5) - 1.) * pi / 6.);
  q = Rot2D (q, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 1.;
  colG = 0.4 * mix (vec3 (0.8, 1., 0.5), vec3 (0.7, 0.9, 0.5), 0.2 +
     0.8 * smoothstep (0.3, 0.7, Fbm2 (4. * ro.xz)));
  vf = vec2 (0.);
  ff = 1. - smoothstep (0.5, 0.8, dstGrnd / dstFar);
  f = abs (length (q) - 0.5) - trWidf;
  if (f < 0.) {
    col = mix (vec3 (0.25) * (0.5 + 0.5 * Fbm2 (64. * ro.xz)), vec3 (0.8), f);
    a = abs (mod (4. * (mod (15. * atan (q.y, - q.x) / (2. * pi), 1.) - 0.5) + 0.5, 1.) - 0.5) - 0.3;
    for (float s = 1.; s >= 0.; s -= 1.) {
      col = mix (vec3 (0.9), col, max (smoothstep (0., 0.02, a - 0.03 * s),
         smoothstep (0., 0.002, abs (f + 0.02) - 0.005)));
    }
    col = mix (colG, col, ff);
    vf = vec2 (16., 1. * (1. - 0.9 * f));
  } else {
    col = colG;
    vf = vec2 (4., 1.);
  }
  vn = vec3 (0., 1., 0.);
  if (vf.x > 0. && ff > 0.) vn = VaryNf (vf.x * ro, vn, vf.y * ff);
  col *= 0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.);
  return col;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = vec3 (0.3, 0.4, 0.5);
  } else {
    q = 0.01 * (ro.xz + 2. * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.3, 0.4, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) *
       min (0.3 * pow (sd, 64.) + 0.5 * pow (sd, 2048.), 1.);
    col = mix (skCol, clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 roo, rdo, col, colR, vn;
  float dstObj, dstGrnd, dstTrObj, nDotL, refFac, sh;
  roo = ro;
  rdo = rd;
  dstTrObj = TrObjRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col4 = CarCol (refFac);
    if (dstTrObj < dstObj) refFac = 0.;
    if (col4.a >= 0.) {
      nDotL = max (dot (vn, sunDir), 0.);
      sh = ObjSShadow (ro + 0.01 * vn, sunDir);
      col = col4.rgb * (0.2 + 0.2 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.8 * sh * nDotL * nDotL) + col4.a * step (0.95, sh) *
         pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
      if (refFac > 0.) rd = reflect (rd, vn);
    } else if (col4.a == -1.) {
      col = col4.rgb * (0.2 - 0.8 * dot (rd, vn));
    }
  }
  if (dstObj >= dstFar || refFac > 0.) {
    if (rd.y < 0.) {
      dstGrnd = - ro.y / rd.y;
      ro += dstGrnd * rd;
      vn = vec3 (0., 1., 0.);
      sh = (dstGrnd < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
      colR = GrndCol (vec3 (ro.xz + carPos.xz, 0.).xzy, dstGrnd, sh);
      colR = mix (colR, vec3 (0.3, 0.4, 0.5), pow (1. + rd.y, 16.));
    } else {
      colR = SkyBgCol (ro, rd);
    }
    col = (refFac > 0.) ? mix (col, 0.9 * colR, refFac) : colR;
  }
  if (dstTrObj < min (dstObj, dstFar)) {
    ro = roo + dstTrObj * rdo;
    vn = TrObjNf (ro);
    col *= vec3 (0.9, 1., 0.9);
    rd = reflect (rdo, vn);
    col = mix (col, SkyBgCol (ro, rd), 0.2 + 0.8 * pow (1. - abs (dot (vn, rdo)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat;
  vec3 rd, ro, col, wgBox;
  vec2 canvas, uv, ud, cnPos;
  float el, az, asp, zmFac, onPath, trvDist, s, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  carPos = stDat.xyz;
  carRot = stDat.w;
  stDat = Loadv4 (1);
  wlBase = stDat.xyz;
  wlRad = wlBase.y;
  stDat = Loadv4 (2);
  strRot = stDat.y;
  wlRot = stDat.zw;
  stDat = Loadv4 (3);
  az = stDat.x;
  el = stDat.y;
  cnPos = stDat.zw;
  stDat = Loadv4 (4);
  hgSize = stDat.x;
  trWidf = stDat.y;
  onPath = stDat.z;
  trvDist = stDat.w;
  el = clamp (el - 0.1 * pi, -0.49 * pi, -0.03 * pi);
  if (el > -0.47 * pi) az += carRot;
  vuMat = StdVuMat (el, az);
