float OnPath ()
{
  vec2 q, gId;
  q = carPos.xz + Rot2D (vec2 (0., wlBase.z), - carRot);
  gId = PixToHex (q / hgSize);
  q = Rot2D ((q - HexToPix (gId * hgSize)) / hgSize,
     (2. * step (Hashfv2 (gId), 0.5) - 1.) * pi / 6.);
  q = Rot2D (q, 2. * pi * floor (3. * atan (q.y, - q.x) / (2. * pi) + 0.5) / 3.);
  q.x += 1.;
  return (abs (length (q) - 0.5) < trWidf) ? 1. : 0.;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
