    strRot = 0.;
    wlRad = 0.6;
    wlBase = vec3 (1.25, wlRad, 2.);
    wlRot = vec2 (0.);
    az = 0.;
    el = 0.;
    nStep = 0.;
    cnPos = vec2 (0.);
    mPtrP = mPtr;
    wgSel = 0;
    autoMd = 1.;
    hgSize = 40.;
    trWidf = 0.12;
    onPath = 0.;
    trvDist = 0.;
  } else {
    stDat = Loadv4 (0);
    carPos = stDat.xyz;
    carRot = stDat.w;
    stDat = Loadv4 (1);
