#define getParticlePos(id) texelFetch(iChannel0, ivec2(id, 0), 0).xy
#define getParticleVel(id) texelFetch(iChannel0, ivec2(id, 1), 0).xy

#define PARTICLES 200
#define PARTICLE_RADIUS 0.01

#define GRAVITY 0.001
#define DECAY 0.95

// Hash from "Rainier mood" by Zavie (https://www.shadertoy.com/view/ldfyzl).
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

