            particleVel.x *= -1.0;
            particleVel *= DECAY;
        }

        if (abs(topRight.y - abs(particlePos.y)) < PARTICLE_RADIUS) {
            particleVel.y *= -1.0;
            particleVel *= DECAY;
        }

        particleVel.y -= GRAVITY;

        particlePos += particleVel;
        particlePos.y = max(PARTICLE_RADIUS - 0.5, particlePos.y);
        if (row == 0) {
            fragColor = vec4(particlePos, 0.0, 1.0);
        }

        if (row == 1) {
            fragColor = vec4(particleVel, 0.0, 1.0);
        }
    }
