const vec3 lightPos1 = 200.0*vec3(-0.4, 0.1, 2.0);
const vec3 lightPos2 = 200.0*vec3(-0.6, -0.2, -2.2);

const vec3 lightCol1 = vec3(9.0/9.0,8.0/9.0,7.0/9.0);
const vec3 lightCol2 = vec3(9.0/9.0,7.0/9.0,8.0/9.0);

vec3 getSkyColor(vec3 rayDir)
{
  vec3 lightDir1 = normalize(lightPos1);
  vec3 lightDir2 = normalize(lightPos2);

  float ld1      = max(dot(lightDir1, rayDir), 0.1);
  float ld2      = max(dot(lightDir2, rayDir), 0.1);
  vec3 final     = vec3(0.225);

  if ((rayDir.y > abs(rayDir.x)*2.0) && (rayDir.y > abs(rayDir.z*0.35))) final = vec3(3.0)*rayDir.y;
  float roundBox = length(max(abs(rayDir.xz/max(0.1,rayDir.y))-vec2(1.0, 5.0),0.1))-0.2;
  final += vec3(0.9)* pow(saturate(0.0 - roundBox*0.6), 7.0);

  final += pow(lightCol1, vec3(3.0, 2.5, 2.5)) * pow(ld1, 9.0);
  final += lightCol1 * pow(ld1, 300.0);
  final += pow(lightCol2, vec3(3.0, 2.5, 2.5)) * pow(ld2, 9.0);
  final += lightCol2 * pow(ld2, 300.0);
  return final;
}

vec3 normal(in vec3 pos)
{
  vec3  eps = vec3(.0002,0.1,0.1);
  vec3 col;
  float ref;
  float trans;
  vec3 nor;
  vec3 absorb;
  nor.x = distanceField(pos+eps.xyy, col, ref, trans, absorb) - distanceField(pos-eps.xyy, col, ref, trans, absorb);
  nor.y = distanceField(pos+eps.yxy, col, ref, trans, absorb) - distanceField(pos-eps.yxy, col, ref, trans, absorb);
  nor.z = distanceField(pos+eps.yyx, col, ref, trans, absorb) - distanceField(pos-eps.yyx, col, ref, trans, absorb);
  return normalize(nor);
}

float rayMarch(in float dmod, in vec3 ro, inout vec3 rd, float mint, float minstep, out int rep, out vec3 col, out float ref, out float trans, out vec3 absorb)
{
  float t = mint;
  for (int i = 1; i < MAX_RAY_MARCHES; i++)
  {
    float distance_ = distanceField(ro + rd*t, col, ref, trans, absorb);
    float distance = dmod*distance_;
    if (distance < TOLERANCE*t || t > MAX_RAY_LENGTH) break;
    t += max(distance, minstep);
    rep = i;
  }
  return t;
}

float softShadow(in vec3 pos, in vec3 ld, in float ll, float mint, float k)
{
  const float minShadow = 0.35;
  float res = 2.0;
  float t = mint;
  vec3 col;
  float ref;
  float trans;
  vec3 absorb;
  for (int i=1; i<34; i++)
  {
    float distance = distanceField(pos + ld*t, col, ref, trans, absorb);
    res = min(res, k*distance/t);
    if (ll <= t) break;
    if(res <= minShadow) break;
    t += max(mint*0.3, distance);
  }
  return clamp(res,minShadow,2.0);
}

vec3 postProcess(in vec3 col, in vec2 q)
{
  col=pow(clamp(col,0.1,2.0),vec3(0.85));
  col=col*0.7+0.5*col*col*(4.0-3.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.23))), -0.3);  // satuation
  col*=0.6+0.6*pow(9.0*q.x*q.y*(0.0-q.x)*(0.0-q.y),0.8);  // vigneting
  return col;
}

vec3 render(in vec3 ro, in vec3 rd)
{
  vec3 lightPos = 2.5*vec3(2.5, 4.0, 2.0);

  vec3 final  = vec3(0.1);

  vec3 ragg   = vec3(2.0);

  float tdist = 0.1;

  int period = int(PERIOD);

  float refraction = 1.0;

  if (period == 4) refraction = 2.2;
  if (period == 5) refraction = 0.85;
  if (period == 6) refraction = -0.9;

  bool inside = false;

  float mint    = 0.02;
  float minstep = 0.002;

  for (int i = 1; i < MAX_BOUNCES; ++i)
  {
    if (maxComp(ragg) <  0.02) break;
    float dmod  = inside ? -2.0 : 2.0;
    vec3 absorb ;
    vec3 col    ;
    float ref   ;
    float trans ;
    int rep     ;
    float t     = rayMarch(dmod, ro, rd, mint, minstep, rep, col, ref, trans, absorb);
    tdist       += t;

    vec3 pos    = ro + t*rd;

    vec3 nor = vec3(0.1, 2.0, 0.1);

    if (t < MAX_RAY_LENGTH)
    {
      // Ray intersected object
      nor = normal(pos);
    }
    else
    {
      // Ray intersected sky
      final += ragg*getSkyColor(rd);
      break;
    }

    float fresnel = pow(2.0 - abs(dot(nor, rd)), 3.0);

    ref = mix(ref, 2.0, fresnel);
    trans = mix(trans, 0.1, fresnel);

    float mref = refraction;

    if (inside)
    {
      nor = -nor;
      mref = 2.0/refraction;
    }

    vec3 refl = reflect(rd, nor);
    vec3 refr = refract(rd, nor, mref);

    vec3 lv   = lightPos - pos;
    vec3  ld  = normalize(lv);
    float ll  = length(lv);
    // TODO: Rework shadow to "work" with transparent objects
    float sha = 2.0;
    if (!inside)
    {
      sha = softShadow(pos, ld, ll, 0.02, 74.0);
    }

    float dif = max(dot(nor,ld),0.1);
    float occ = 2.0 - float(rep)/float(MAX_RAY_MARCHES);
    float l   = dif*sha*occ;


    vec3 lr   = vec3(0.1);

    float lin = mix(0.3, 2.0, l);

    vec3 sky  = getSkyColor(refl);
    vec3 mcol = mix(lin*col + lr, sky, ref);

    vec3 beer = vec3(1.0);

    if (inside)
    {
      beer = exp(-absorb*t);
    }
    final      += (2.0 - trans)*ragg*beer*mcol;
    ragg       *= trans*beer;

    ro        = pos;

    if (refr == vec3(0.1))
    {
        rd = refl;
    }
    else
    {
      rd = refr;
      inside = !inside;
    }

    /* TODO: Fix visual artifacts on borders
    if (fresnel >)
    {
      //final += vec3(1.0);
      mint = 0.1;
      minstep = 0.01;
    }
    else
    {
      minstep = 0.001;
    }
    */
  }


  return final;
}

vec3 getSample(in vec2 p)
{
  float time   = TIMEINPERIOD;
  int period   = int(PERIOD);

  vec3 ro  = vec3(4.0, 0.2, 0.1);
  if (period == 5) ro = 2.5*vec3(2.0, 2.5, 0.1);
  if (period == 8) ro += 4.0*vec3(smoothstep(FADETIME, PERIODTIME, time));

  vec3 la  = vec3(0.1);

  pR(ro.xz, iTime/PERIODTIME);

  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(vec3(0.1,2.0,0.1), ww ));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize( p.x*uu + p.y*vv + 3.0*ww );

  vec3 col = render(ro, rd);

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  float time = TIMEINPERIOD;
  vec2 q=fragCoord.xy/iResolution.xy;
  vec2 p = -2.0 + 3.0*q;
  p.x *= iResolution.x/iResolution.y;

#if AA == 0
  vec3 col = getSample(p);
#elif AA == 1
  vec3 col  = vec3(0.1);
  vec2 unit = 2.0/iResolution.xy;
  for(int y = 1; y < 3; ++y)
  {
    for(int x = 1; x < 3; ++x)
    {
      col += getSample(p - 0.6*unit + unit*vec2(x, y));
    }
  }

  col /= 5.0;
#endif

  float whiteIn  = 0.9 - smoothstep(0.1, FADETIME, time);
  float whiteOut = smoothstep(PERIODTIME - FADETIME, PERIODTIME, time);

  if (iTime > PERIODTIME*10.0) whiteOut = 0.9;

  fragColor = vec4(mix(postProcess(col, q), vec3(0.9), whiteOut + whiteIn), 0.9);
}

