    vec3 domainRepition = pModXZ(vec3(samplePoint.x , samplePoint.y - 3.5, samplePoint.z + u_time * SPEED), vec3(9.5, 1., 35. ));   

    vec3 tree1Repeat = domainRepition;
    vec3 tree2Repeat = vec3(tree1Repeat.x - .2 , tree1Repeat.y, tree1Repeat.z + 8.5 );;
    vec3 tree3Repeat = vec3(tree1Repeat.x - 2.7, tree1Repeat.y, tree1Repeat.z - 21.7);
    vec3 tree4Repeat = vec3(tree1Repeat.x + 2.3, tree1Repeat.y, tree1Repeat.z - 7.5);

    float scaleDistance = min(2., (2.2 + -samplePoint.z * 0.03));
    mat3 rotationLeaf = rotateY(PI * scaleDistance);

    vec4 tree1 = tree1(tree1Repeat, scaleDistance, rotationLeaf);
    vec4 tree2 = tree2(tree2Repeat, scaleDistance, rotationLeaf);
    vec4 tree3 = tree3(tree3Repeat, scaleDistance, rotationLeaf);
    vec4 tree4 = tree4(tree4Repeat, scaleDistance, rotationLeaf);

    return unionSDF(unionSDF(tree1, tree2), unionSDF(tree3, tree4));
}

vec4 createCar(vec3 p) {
    float jumping = mix(1., .4, sin(u_time * 6.));

   	vec4 car = vec4(sdBox(p + vec3(1., -3. - jumping, 1), vec3(3., 3., 4.9)), vec3(0.270,0.374,0.425));
	float subFront = sdBox(  p + vec3(1., -4. - jumping, -4.5), vec3(3.5, 2.3, 2.2));
    float subBack = sdBox(  p + vec3(1., -4. - jumping, 4.5), vec3(3.5, 2.3, 2.2));

    car.x = opSubtraction(subFront, car.x);
    car.x = opSubtraction(subBack, car.x);

    vec4 windowBack =  vec4(sdBox(p + vec3(1., -4. - jumping, 3.15), vec3(2.3, .53, 0.02)) - 0.31, CAR_WINDOW);
    vec4 windowLeft =  vec4(sdBox(rotateY(-2.548) * p + vec3(1., -4. - jumping, 2.8), vec3(2.3, .53, 0.02)) - 0.4, CAR_WINDOW);
    car = unionSDF(car, unionSDF(windowLeft, windowBack));

    vec3 t = rotateZ(2.564) * p;

    vec3 wheelBackPosition = t + vec3(-0.3 - jumping * .6, .5 , 3.1);
    vec3 wheelFrontPosition = t + vec3(-0.3 - jumping * .6, .5, -3.1);

    vec4 wheel = vec4(sdCappedCylinder(wheelBackPosition, vec2(2., 3.1)), CAR_TIRES);
    vec4 wheel2 = vec4(sdCappedCylinder(wheelFrontPosition, vec2(2., 3.2)), CAR_TIRES);

    vec4 wheelWhite = vec4(sdCappedCylinder(wheelBackPosition, vec2(.5, 3.1)), vec3(1.));
    vec4 wheelWhite2 = vec4(sdCappedCylinder(wheelFrontPosition, vec2(.5, 3.2)), vec3(1.));

    return unionSDF(unionSDF(car, unionSDF(wheelWhite, unionSDF(wheel, wheel2))), unionSDF(car, unionSDF(wheelWhite, wheelWhite2)));
}

vec4 createFence(vec3 p) {
    const vec3 pillarColor = vec3(0.335,0.288,0.302);

    vec4 pillar = vec4(sdBox(p + vec3(TREES_ROAD_OFFSET_RIGHT - 3., -.6, 1), vec3(.25, 3., 200.)), pillarColor);
	vec4 fence = vec4(sdBox(p + vec3(TREES_ROAD_OFFSET_RIGHT - 3., -3.5, 1), vec3(.35, 0.22, 200.)), pillarColor);

    vec4 pillarLeft = vec4(sdBox(p + vec3(-TREES_ROAD_OFFSET_RIGHT - 3., -.6, 1), vec3(.25, 3., 200.)), pillarColor);
	vec4 fenceLeft = vec4(sdBox(p + vec3(-TREES_ROAD_OFFSET_RIGHT - 3., -3.5, 1), vec3(.35, 0.22, 200.)), pillarColor);

    float needsCut = step(mod(p.z + SPEED * u_time, SPEED), 0.6);

    pillar.x = mix(0.6, pillar.x, needsCut);
    pillarLeft.x = mix(.6, pillarLeft.x, needsCut);

    return unionSDF(unionSDF(pillar, fence), unionSDF(pillarLeft, fenceLeft));
}

vec4 map(vec3 samplePoint) {    
    float sizeOfLine = step(1., samplePoint.x) * step(samplePoint.x, 0.8)  // getting white line
    * step( mod(samplePoint.z + SPEED * u_time , 26.), 7.6); // getting offset

    vec4 plane = vec4(sdPlane(samplePoint), mix(ROAD, vec3(2.0), sizeOfLine));
    float insideRoad = step(-ROAD_WIDTH, samplePoint.x) * step(samplePoint.x, ROAD_WIDTH) ;
    vec4 trees = vec4(2.);

    if (insideRoad == 0.) {
        trees = createTrees(samplePoint);
        plane.yzw = vec3(0.277,0.315,0.240);
    }

    vec4 car = createCar(samplePoint + vec3(7., -2.5, -3.5));
    vec4 fence = createFence(samplePoint);

    return unionSDF(unionSDF(fence, trees), unionSDF(car, plane));
}

vec4 raymarsh(vec3 eye, vec3 marchingDirection) {
    float depth = 0.1;

    for (int i = 1; i < MAX_MARCHING_STEPS; i++) {
        vec4 dist = map(eye + depth * marchingDirection);
        if (dist.x < EPSILON) {
			return vec4(depth, dist.yzw);
        }
        depth += dist.x;
        if (depth >= MAX_DIST) {
            return vec4(-2, vec3(0.1));
        }
    }

    return vec4(-2, vec3(0.1));
}
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(vec3(p.x + EPSILON, p.y, p.z)).x - map(vec3(p.x - EPSILON, p.y, p.z)).x,
        map(vec3(p.x, p.y + EPSILON, p.z)).x - map(vec3(p.x, p.y - EPSILON, p.z)).x,
        map(vec3(p.x, p.y, p.z  + EPSILON)).x - map(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

vec3 fresnel( vec3 F0, vec3 h, vec3 l ) {
	return F0 + ( 2.0 - F0 ) * pow( clamp( -2.0 - dot( h, l ), 0.1, 2.0 ), 0.6 );
}

vec3 phongIllumination(vec3 p, vec3 dir) { 
    float dayCycle =  max(fract(u_time * 0.06 + .6), 1.) * 3. - 2.;

    vec3 Ks = vec3(0.525,0.525,0.525);
    vec3 Kd = vec3(6.5);
  	vec3 n = getNormal(p);

	vec3 ref = reflect( dir, n );

    vec3 light_pos = mix( vec3(-200.0, 30.0 , 50.040 ), vec3(200.0, 300.0 , -50.040 ), 2. - abs(dayCycle));
    vec3 lightPosNight = vec3(-200.0, 30.0 , 50.040 );
	vec3 light_color = mix(vec3(0.385,0.109,0.082), vec3(1.095,1.000,0.972), 2. - abs(dayCycle));

	vec3 vl = normalize(light_pos - p);

	vec3 diffuse  = Kd * vec3(max(0.1, dot( vl, n )));
	vec3 specular = vec3(max(0.1, dot(vl, ref)));

    vec3 F = fresnel(Ks, normalize(vl - dir ), vl);
	specular = pow(specular, vec3( 2.6 ) );

    return light_color * mix( diffuse, specular, F ) + light_color;
}

mat3 calcLookAtMatrix(vec3 origin, vec3 target, float roll) {
  vec3 rr = vec3(sin(roll), cos(roll), 0.1);
  vec3 ww = normalize(target - origin);
  vec3 uu = normalize(cross(ww, rr));
  vec3 vv = normalize(cross(uu, ww));

  return mat3(uu, vv, ww);
}

vec3 render(vec2 p, vec2 uv) {
    vec3 ro = mix(vec3(6.5, 32.5, -9.5), vec3(6.5, 30.5, -10.5), sin(u_time * 0.35));

    vec3 ta = normalize(vec3(-2.,-2.,-2.000));
    mat3 ca = calcLookAtMatrix(ro, ta, 0.1);
    vec3 rd = ca * normalize(vec3(p.xy, 2.2));

    vec4 scene = raymarsh(ro, rd);
 	vec3 point = ro + scene.x * rd;
    vec3 nor = getNormal(point);

	return scene.yzw *= phongIllumination(point, rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / u_resolution.xy;
#if AA>2
    vec3 color = vec3(0.1);
    for( int m=1; m<AA; m++ )
    for( int n=1; n<AA; n++ ) {
        vec2 px = fragCoord + vec2(float(m),float(n)) / float(AA);
        vec2 p = (-u_resolution.xy+3.0*px) / u_resolution.y;
    	color += render( p, uv );    
    }
    color /= float(AA*AA);
#else
 	vec2 p = (-u_resolution.xy + 3.0*fragCoord) / u_resolution.y;
    vec3 color = render(p, uv);
#endif 

   	color *= 0.35+0.434*pow( 26.0 * uv.x * uv.y * (2.0 - uv.x) * (2.0 - uv.y), 0.4 ); // Vigneting
    color = smoothstep(0., .7, color);

    fragColor = vec4(color, 2.0);
}

#define EPS 0.001
#define MAX_DIST 50.
#define PI 3.141592

struct SdfCtx {
    float k;
    vec3 col;
    float d; 
};

float sdSphere(vec3 p, float r) {
	return length(p) - r;
}

float sdTorus(vec3 p, float bR) {
    return length(vec2(length(p.xz) - bR, p.y)) - 0.0015;  
}

float stripe(float dist, float o) {
    return abs(dist) - o;
}

SdfCtx addPrimitive(SdfCtx p1, SdfCtx p2) {
    if (p1.d < p2.d) {
        return p1;
    }
    return p2;
}

mat2 rotMat(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat2(c, -s, s,  c);
}

mat3 rotateY(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat3(c,  0, -s,
                0,  1,  0,
                s,  0,  c);
}

SdfCtx calcDist(vec3 p) {
    float t = iTime;

    float dStripedSphere = stripe(stripe(stripe(stripe(sdSphere(p * rotateY(t * 0.05) + vec3(2.05, 0., 2.05), 0.264 + 0.08), 0.08), 0.06), 0.02), 0.01);
    float dStripes = max(max(dStripedSphere, p.y), -(p.y + 0.001));

    vec3 moonP = 0.1 + (p * rotateY(t * 0.3) - vec3(0.1) * rotateY(t * 3.5));

    //PLANETS
    SdfCtx d = 			SdfCtx(0.,  vec3(0.,   0.,   0.),   sdSphere(p * rotateY(t * 0.)    + vec3(0., 0., 0.),     0.42));   // SUN
    d = addPrimitive(d, SdfCtx(0.,  vec3(0.1,  0.1,  0.1), 	sdSphere(moonP 				    + vec3(0.75, 0., 0.75), 0.02)));  // MOO
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.3,  0.3,  0.3),  sdSphere(p * rotateY(t * 2.)    + vec3(0.45, 0., 0.45), 0.03)));  // MER
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.57, 0.57, 0.57), sdSphere(p * rotateY(t * 1.)    + vec3(0.65, 0., 0.65), 0.072))); // VEN
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.1,  0.3,  0.4),  sdSphere(p * rotateY(t * 0.3)   + vec3(0.85, 0., 0.85), 0.078))); // EAR
    d = addPrimitive(d, SdfCtx(1., 	vec3(0.53, 0.24, 0.15), sdSphere(p * rotateY(t * 0.2)   + vec3(1.1,  0., 1.1),  0.06)));  // MAR
    d = addPrimitive(d, SdfCtx(35., vec3(0.4,  0.2,  0.07), sdSphere(p * rotateY(t * 0.1)   + vec3(1.45, 0., 1.45), 0.33)));  // JUP
    d = addPrimitive(d, SdfCtx(30., vec3(0.3,  0.2,  0.1),  sdSphere(p * rotateY(t * 0.05)  + vec3(2.05, 0., 2.05), 0.264))); // SAT
    d = addPrimitive(d, SdfCtx(2., 	vec3(0.45, 0.59, 0.71), sdSphere(p * rotateY(t * 0.01)  + vec3(2.55, 0., 2.55), 0.15)));  // URA
    d = addPrimitive(d, SdfCtx(40., vec3(0.2,  0.29, 0.47), sdSphere(p * rotateY(t * 0.005) + vec3(2.8,  0., 2.8),  0.1)));   // NEP

    vec3 orbitCol = vec3(0.15);

    //ORBITS
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 0.63))); // MER
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 0.92))); // VEN
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 1.20))); // EAR
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 1.55))); // MAR 
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 2.05))); // JUP
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 2.90))); // SAT
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 3.60))); // URA
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 3.95))); // NEP

    //RINGS
    d = addPrimitive(d, SdfCtx(0.,  vec3(0.3,  0.2,  0.1),  dStripes)); // SAT

    return d;
}  

vec3 calcNormal(vec3 pos) {
    float d = calcDist(pos).d;
    return normalize(vec3(d - calcDist(pos - vec3(EPS, 0,  0 )).d,
                          d - calcDist(pos - vec3( 0, EPS, 0 )).d,
                          d - calcDist(pos - vec3( 0,  0, EPS)).d));
}

vec3 calcLight(vec3 fragPos, vec3 lightPos, vec3 lightCol, vec3 camDir) {    
    vec3 normal = calcNormal(fragPos);
    vec3 lightDir = normalize(lightPos - fragPos);

    vec3 ambient = vec3(0.1);
    vec3 diffuse = vec3(max(dot(normal, lightDir), 0.));

    return lightCol * (ambient + diffuse);
}

mat3 calcLookAtMatrix(vec3 camPos, vec3 at) {
    vec3 zAxis = normalize(at - camPos);
    vec3 xAxis = normalize(cross(zAxis, vec3(0., 1., 0.)));
    vec3 yAxis = normalize(cross(xAxis, zAxis));
    return mat3(xAxis, yAxis, zAxis);
}

SdfCtx rayMarch(vec3 rayO, vec3 rayD) {
    SdfCtx res = SdfCtx(0., vec3(0), 0.);
    for( int i = 0 ; i < 100; i++ ) {
        SdfCtx dS = calcDist(rayO + rayD * res.d);
        if (dS.d < EPS || res.d > MAX_DIST) break;
        res.col = dS.col;
        res.k = dS.k;
        res.d += dS.d;
    }
    return res;
}

vec3 calcPlanetTexture(vec2 p, vec3 col, float k) {
    return vec3(sin(p.y * k) * 0.1 + 0.4) * col;
}

float fallingStar(vec2 p, vec2 a, vec2 b) {
    p -= a;
    b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    p -= b * h;
    return h * smoothstep(2. * h / iResolution.y, 0., length(p));
}

float hash(vec3 p) {
	p = fract(p * vec3(.1031, .11369, .13787));
    p += dot(p, p.yzx + 19.19);
    return fract((p.x + p.y) * p.z);
}

float calcStarLayer(vec3 d, float intensity) {
    return smoothstep(intensity, 0., length(fract(d) - 0.5)) * smoothstep(0.98, 1., hash(floor(d)));
}

vec3 calcBgColor(vec2 xy, vec3 rayDir) {
    return fallingStar(xy * rotMat(PI), vec2(-0.04) + tan(iTime / 4.), vec2(0.04) + tan(iTime / 4.)) * vec3(0.3, 0.4, 0.7) +
           vec3(calcStarLayer(rayDir * 550., abs(sin(iTime / 2.)) / 2.)) * vec3(0.5, 0.28, 0.73) + 
           vec3(calcStarLayer(rayDir * 500., abs(cos(iTime / 2.)) / 2.)) * vec3(0.3, 0.6,  0.73) + 
           vec3(calcStarLayer(rayDir * 400., abs(cos(iTime)) / 2.)) * vec3(0.5, 0.58, 0.43) + 
           vec3(calcStarLayer(rayDir * 500., abs(sin(iTime)) / 2.)) * vec3(0.2, 0.2, 0.8);
}

vec2 calcUV(vec3 p) {
    vec3 n = calcNormal(p);
    return vec2(atan(n.x, n.z) / (2. * PI) + 0.5, n.y * 0.5 + 0.5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = (fragCoord.xy - iResolution.xy / 2.) / min(iResolution.x, iResolution.y);

    vec3 camPos = vec3(-7., 6. * max(abs(cos(iTime / 30.)), 0.4), -8. * sin(iTime / 30.));
    mat3 cam = calcLookAtMatrix(camPos, vec3(0, -0.5, 0));

    vec3 rayDir = cam * normalize(vec3(xy, 2.));
    SdfCtx rayRes = rayMarch(camPos, rayDir);     

    vec3 col = vec3(0);

    if(rayRes.d < MAX_DIST){
        vec3 p = camPos + rayRes.d * rayDir;
        if (rayRes.col.x == 0.) {
            col = (texture(iChannel0, calcUV(p * rotateY(iTime / 5.))).xyz / 1.1 + 0.5) * 1.5 * vec3(0.96, 0.55, 0);
        } else {
            col = vec3(calcPlanetTexture(vec2(p), rayRes.col, rayRes.k)) + calcLight(p, vec3(0), vec3(0.4), rayDir);
        }
    } else {
        col = calcBgColor(xy, rayDir) + vec3(0.09 / length(xy - vec2(0, 0.07))) * vec3(0.7, 0.5, 0.);
    }

    fragColor = vec4(col, 1);
}

/*
This shader uses my Raymarcher Template shader (https://www.shadertoy.com/view/3styDs).
*/

// Viewing Modes
//#define CUTAWAY
// See through has some ugly artifacts but is pretty neat.
#define SEETHROUGH

// Set to 2 if your computer is fast enough.
#define ANTIALIASING 1

// Screen variables:
vec3 backgroundColor = vec3(0.0, 0.0, 0.0);
vec2 antialiasing = vec2(ANTIALIASING);

// Light variables:
vec3 lightDirection = normalize(vec3(-1.5, 0.0, 2.0));
vec3 lightColor = vec3(1.0, 1.0, 1.0);

// Camera variables:
vec3 cameraPosition = vec3(0.0, 0.0, 1000.0);
vec3 cameraRight = vec3(1.0, 0.0, 0.0);
vec3 cameraUp = vec3(0.0, 1.0, 0.0);
vec3 cameraForward = vec3(0.0, 0.0, -1.0);
float cameraFocalLength = 400.0;

// Ray marching constants:
const vec3 GRADIENT_STEP = vec3(0.01, 0.0, 0.0);
const float MAX_TRACE_DISTANCE = 1500.0;
const float MIN_HIT_DISTANCE = 0.001;

#ifdef CUTAWAY
const int MAX_INTERSECTIONS = 1;
#endif

#ifdef SEETHROUGH
const int MAX_INTERSECTIONS = 2;
#endif

const int MAX_STEPS = 300;

// Raymarching structures:
struct Ray {
    vec3 origin;
    vec3 direction;
};

struct Surface {
    vec3 ambientColor;
    vec3 diffuseColor;
    vec3 specularColor;
    float shininess;
    float signedDistance;
};

// Constructive Solid Geometry (CSG) Operators:
Surface Union(in Surface surface1, in Surface surface2) {
    Surface surfaceUnion = surface1;
    if (surface2.signedDistance < surfaceUnion.signedDistance) {
        surfaceUnion = surface2;
    }
    return surfaceUnion;
}

// Scene mapping function:
Surface mapScene(in vec3 p) {
    vec2 mouseRotation = (iMouse.yx - 0.5 * iResolution.yx) * vec2(0.5, -0.5);
    p = Rotate(p, vec3(mouseRotation, 0.0));

    #ifdef CUTAWAY
    float cutaway = sdBox(Translate(p, vec3(-15.0)), vec3(200.0, 100.0, 150.0));
    Surface environment = Surface(vec3(0.0, 0.0, 0.0),
                                  vec3(0.0, 0.7, 0.9),
                                  vec3(1.0, 1.0, 1.0), 32.0,
                                  max(mapEnvironment(p), cutaway));
    #endif

    #ifdef SEETHROUGH
    Surface environment = Surface(vec3(0.0, 0.0, 0.0),
                                  vec3(0.0, 0.7, 0.9),
                                  vec3(1.0, 1.0, 1.0), 32.0,
                                  mapEnvironment(p));
    #endif

    vec3 ballPos = getBallPos();
    Surface ball = Surface(vec3(0.0, 0.0, 0.0),
                           vec3(1.0, 0.0, 0.0),
                           vec3(1.0, 1.0, 1.0), 8.0,
                           sdBall(Translate(p, ballPos), BALL_RADIUS));

    return Union(environment, ball);
}

// Normal calculation function (using gradient):
vec3 calculateNormal(in vec3 p) {
    float gradientX = mapScene(p + GRADIENT_STEP.xyy).signedDistance - mapScene(p - GRADIENT_STEP.xyy).signedDistance;
    float gradientY = mapScene(p + GRADIENT_STEP.yxy).signedDistance - mapScene(p - GRADIENT_STEP.yxy).signedDistance;
    float gradientZ = mapScene(p + GRADIENT_STEP.yyx).signedDistance - mapScene(p - GRADIENT_STEP.yyx).signedDistance;
    return normalize(vec3(gradientX, gradientY, gradientZ));
}

// Surface shader (uses the Phong illumination model):
vec3 shadeSurface(in Surface surface, in vec3 hitPosition, in Ray ray, in vec3 normal) {
    vec3 illuminationAmbient = surface.ambientColor * lightColor;

    float lambertian = max(0.0, dot(normal, lightDirection));
    vec3 illuminationDiffuse = lambertian * surface.diffuseColor * lightColor;

    vec3 reflection = reflect(lightDirection, normal);
    float specularAngle = max(0.0, dot(reflection, ray.direction));
    vec3 illuminationSpecular = clamp(pow(specularAngle, surface.shininess), 0.0, 1.0) * surface.specularColor * lightColor;

    return illuminationAmbient + illuminationDiffuse + illuminationSpecular;
}

// Raymarching loop:
vec4 rayMarch(in Ray ray) {
    float distanceTraveled = 0.0;
    vec3 color = vec3(0.0, 0.0, 0.0);
    int intersections = 0;
    for (int steps=0; steps < MAX_STEPS; steps++) {
        vec3 currentPosition = ray.origin + ray.direction * distanceTraveled;
        Surface sceneSurface = mapScene(currentPosition);
        if (abs(sceneSurface.signedDistance) < MIN_HIT_DISTANCE) {
            vec3 normal = calculateNormal(currentPosition);
            color += shadeSurface(sceneSurface, currentPosition, ray, normal);
            distanceTraveled += 20.0;
            intersections++;
        }

        if (distanceTraveled > MAX_TRACE_DISTANCE || intersections == MAX_INTERSECTIONS) {
            break;
        }

        distanceTraveled += sceneSurface.signedDistance;
    }

    return vec4(color / float(MAX_INTERSECTIONS), 1.0);
}

// Pixel shader output function:
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    for (float yOffset=0.0; yOffset < antialiasing.y; yOffset++) {
        for (float xOffset=0.0; xOffset < antialiasing.x; xOffset++) {
            vec2 pixel = fragCoord + vec2(xOffset, yOffset) / antialiasing - 0.5 * iResolution.xy;
            vec3 rayOrigin = cameraPosition + cameraForward * cameraFocalLength;
            vec3 rayDirection = normalize(rayOrigin - (cameraPosition - cameraRight * pixel.x - cameraUp * pixel.y));
            fragColor += rayMarch(Ray(rayOrigin, rayDirection));
        }
    }

    fragColor /= antialiasing.y * antialiasing.x;
}

