        ballPos += ballVel;

        float distToEnv = mapEnvironment(ballPos);
        if (distToEnv < BALL_RADIUS) {
            vec3 hitNormal = getNormal(ballPos);
            ballPos += hitNormal * (BALL_RADIUS - distToEnv);
            ballVel = reflect(ballVel, hitNormal);
            ballVel *= DECAY;
        }

        if (ivec2(fragCoord) == ivec2(0, 0)) {
            fragColor = vec4(ballPos, 1.0);
        }

        if (ivec2(fragCoord) == ivec2(1, 0)) {
            fragColor = vec4(ballVel, 1.0);
        }
    }
}

