#define BALL_RADIUS 10.0

#define GRAVITY 0.1
#define DECAY 0.98

// 3D Box SDF Inigo Quilez's 3D SDFs article (https://iquilezles.org/articles/distfunctions):
float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float sdBall(in vec3 p, in float r) {
    return length(p) - r;
}

// Transformations:
vec3 Translate(in vec3 p, in vec3 t) {
    return p - t;
}

vec3 Rotate(in vec3 p, in vec3 r) {
    vec3 rad = radians(-r);
    vec3 cosRad = cos(rad);
    vec3 sinRad = sin(rad);

    mat3 xRotation = mat3(1.0,      0.0,       0.0,
                          0.0, cosRad.x, -sinRad.x,
                          0.0, sinRad.x,  cosRad.x);

    mat3 yRotation = mat3( cosRad.y, 0.0, sinRad.y,
                                0.0, 1.0,      0.0,
                          -sinRad.y, 0.0, cosRad.y);

    mat3 zRotation = mat3(cosRad.z, -sinRad.z, 0.0,
                          sinRad.z,  cosRad.z, 0.0,
                               0.0,       0.0, 1.0);

    return zRotation * yRotation * xRotation * p;
}

// Functions defining the environment the ball bounces in:
float mapEnvironment(in vec3 p) {
    float wall = abs(sdBox(p, vec3(200.0, 100.0, 150.0))) - 1.0;
    float box = sdBox(Rotate(Translate(p, vec3(100.0, 0.0, 0.0)), vec3(45.0, 35.0, 0.0)), vec3(50.0));
    float ballDisplacement = (sin(p.x * 0.1) + sin(p.y * 0.1) + sin(p.z * 0.1)) * 6.0;
    float ball = sdBall(Translate(p, vec3(-100.0, 0.0, 0.0)), 50.0) + ballDisplacement;
    return min(wall, min(box, ball));
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapEnvironment(p + vec3(0.001, 0.0, 0.0)) - mapEnvironment(p - vec3(0.001, 0.0, 0.0)),
                          mapEnvironment(p + vec3(0.0, 0.001, 0.0)) - mapEnvironment(p - vec3(0.0, 0.001, 0.0)),
                          mapEnvironment(p + vec3(0.0, 0.0, 0.001)) - mapEnvironment(p - vec3(0.0, 0.0, 0.001))));
}

